CREATE OR REPLACE PROCEDURE devolver_adiant_cartao (nr_adiantamento_p bigint, nr_seq_movto_cartao_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w		bigint;
cd_moeda_w		integer;
vl_transacao_w		double precision;


BEGIN 
 
if (nr_adiantamento_p IS NOT NULL AND nr_adiantamento_p::text <> '') and (nr_seq_movto_cartao_p IS NOT NULL AND nr_seq_movto_cartao_p::text <> '') then 
	 
	select	coalesce(max(cd_moeda_padrao),1) 
	into STRICT	cd_moeda_w 
	from	parametro_contas_receber;
 
	select	vl_transacao 
	into STRICT	vl_transacao_w 
	from	movto_cartao_cr 
	where	nr_sequencia	= nr_seq_movto_cartao_p;
 
	select	coalesce(max(nr_sequencia),0) + 1 
	into STRICT	nr_sequencia_w 
	from	adiantamento_dev 
	where	nr_adiantamento	= nr_adiantamento_p;
 
 
	insert into adiantamento_dev( 
		nr_adiantamento, 
		nr_sequencia, 
		dt_devolucao, 
		vl_devolucao, 
		cd_moeda, 
		dt_atualizacao, 
		nm_usuario, 
		ds_motivo_dev, 
		nr_lote_contabil, 
		nr_seq_trans_fin) 
	values (nr_adiantamento_p, 
		nr_sequencia_w, 
		clock_timestamp(), 
		vl_transacao_w, 
		cd_moeda_w, 
		clock_timestamp(), 
		nm_usuario_p, 
		wheb_mensagem_pck.get_texto(305689), 
		null, 
		null);
 
	update	movto_cartao_cr 
	set	dt_liberacao	= clock_timestamp(), 
		nm_usuario	= nm_usuario_p, 
		dt_atualizacao	= clock_timestamp() 
	where	nr_sequencia	= nr_seq_movto_cartao_p;
 
	CALL atualizar_saldo_adiantamento(nr_adiantamento_p,nm_usuario_p, null);
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
