CREATE OR REPLACE PROCEDURE diagnostico_atend_beforepost ( dt_diagnostico_p timestamp, nr_atendimento_p bigint, cd_doenca_p text, ie_lado_p text, nm_usuario_p text) AS $body$
DECLARE


qt_diagnosticos_w	bigint := 0;
ie_lado_w		varchar(1);


BEGIN

select 	count(*)
into STRICT	qt_diagnosticos_w
from 	diagnostico_doenca
where 	dt_diagnostico  = dt_diagnostico_p
and   	nr_atendimento  = nr_atendimento_p
and   	cd_doenca       = cd_doenca_p;

if (qt_diagnosticos_w > 0) then
	--O CID #@CD_DOENCA#@ já foi cadastrado para este atendimento !
	CALL wheb_mensagem_pck.exibir_mensagem_abort(163130, cd_doenca_p);
end if;

select 	ie_exige_lado
into STRICT	ie_lado_w
from 	cid_doenca
where 	cd_doenca_cid = cd_doenca_p;

if (coalesce(ie_lado_p::text, '') = ''  and
	ie_lado_w = 'S') then
	--Este CID exige a informação do Lado.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(163137);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
