CREATE OR REPLACE PROCEDURE diag_doenca_eup_before_post_js ( cd_convenio_p bigint, cd_estabelecimento_p bigint, cd_doenca_cid_p text, ie_tipo_doenca_p text, qt_tempo_p bigint, ie_unidade_tempo_p text, ie_exige_tempo_doenca_p INOUT text, ds_texto_pri_p INOUT text, ds_texto_sec_p INOUT text, ds_obter_cid_infect_p INOUT text, ds_texto_ter_p INOUT text, ie_situacao_p INOUT text, ds_texto_qua_p INOUT text, ds_msg_tiss_p INOUT text) AS $body$
DECLARE

 
ie_exige_tempo_doenca_w		varchar(1);
ds_texto_pri_w			varchar(255);
ds_texto_sec_w			varchar(255);
ds_obter_cid_infect_w		varchar(1);
ds_texto_ter_w			varchar(255);
ie_situacao_w			varchar(1);	
ds_texto_qua_w			varchar(255);	
ds_msg_tiss_w			varchar(255);


BEGIN 
 
if (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '')then 
	begin 
	 
	select coalesce(max(ie_exige_tempo_doenca), 'N') 
	into STRICT	ie_exige_tempo_doenca_w 
	from 	convenio_estabelecimento 
	where  cd_convenio = cd_convenio_p 
	and   cd_estabelecimento = cd_estabelecimento_p;
	 
	ds_texto_pri_w	:= obter_texto_tasy(68674, wheb_usuario_pck.get_nr_seq_idioma);
	ds_texto_sec_w	:= obter_texto_tasy(68683, wheb_usuario_pck.get_nr_seq_idioma);
	 
	ds_obter_cid_infect_w	:= obter_cid_infectocontagiosa(cd_doenca_cid_p);
	 
	ds_texto_ter_w	:= obter_texto_tasy(68682, wheb_usuario_pck.get_nr_seq_idioma);
	 
	select 	max(ie_situacao) 
	into STRICT	ie_situacao_w 
	from 	cid_doenca 
	where 	cd_doenca_cid = cd_doenca_cid_p;
	 
	ds_texto_qua_w	:= obter_texto_tasy(68689, wheb_usuario_pck.get_nr_seq_idioma);
	 
	end;
end if;
 
ds_msg_tiss_w := consistir_inf_tiss_eup(ie_tipo_doenca_p, qt_tempo_p, ie_unidade_tempo_p, ds_msg_tiss_w);
 
ie_exige_tempo_doenca_p	:= ie_exige_tempo_doenca_w;
ds_texto_pri_p		:= ds_texto_pri_w;
ds_texto_sec_p		:= ds_texto_sec_w;
ds_obter_cid_infect_p	:= ds_obter_cid_infect_w;
ds_texto_ter_p		:= ds_texto_ter_w;
ie_situacao_p		:= ie_situacao_w;
ds_texto_qua_p		:= ds_texto_qua_w;
ds_msg_tiss_p      := ds_msg_tiss_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
