ds_arquivo_w			varchar(4000);
ds_linha_w			varchar(8000);
ds_quadro_w			varchar(2);
nr_linha_w			bigint 	:= qt_linha_p;
nr_seq_registro_w		bigint 	:= nr_sequencia_p;
separador_w			varchar(1) 	:= ds_separador_p;
tp_registro_w			varchar(2);

nr_inscricao_w			varchar(9);
nm_contribuinte_w		varchar(50);
cd_cgc_w			varchar(14);
dt_referencia_w			varchar(6);
ie_tipo_declaracao_w		varchar(1);
ie_regime_apuracao_w		varchar(1);
ie_porte_empresa_w		varchar(1);
ie_consolidada_w		varchar(1);
ie_centralizada_w		varchar(1);
ie_transf_creditos_w		varchar(1);
ie_creditos_presu_w		varchar(1);
ie_incentivos_fiscais_w		varchar(1);
ie_movimento_w			varchar(1);
ie_substituto_w			varchar(1);
ie_escrita_w			varchar(1);
nr_trabalhadores_w		varchar(5);
nr_seq_dime_w			fis_dime.nr_sequencia%type;
nr_seq_regra_dime_w		fis_dime_controle.nr_seq_regra_dime%type;


BEGIN 
select	cd_cgc 
into STRICT	cd_cgc_w 
from	estabelecimento 
where	cd_estabelecimento	= cd_estabelecimento_p;
 
select	nr_seq_regra_dime 
into STRICT	nr_seq_regra_dime_w 
from	fis_dime_controle 
where	nr_sequencia	= nr_seq_controle_p;
 
select	'21', 
	'00', 
	substr(obter_dados_pf_pj('',cd_cgc_w,'IE'),1,9), 
	rpad(obter_dados_pf_pj('',cd_cgc_w,'N'),50,' '), 
	to_char(fim_mes(dt_referencia_p),'mmyyyy'), 
	ie_tipo_declaracao, 
	ie_regime_apuracao, 
	ie_porte_empresa, 
	ie_consolidada, 
	'1' ie_centralizada, 
	ie_transf_creditos, 
	CASE WHEN ie_credito_presu='S' THEN 1  ELSE 2 END , 
	'1' ie_incentivos_fiscais, 
	ie_movimento, 
	ie_subst_tributario, 
	ie_escrita_contabil, 
	substr(lpad(nr_trabalhadores,5,0),1,5), 
	nr_sequencia 
into STRICT	tp_registro_w, 
	ds_quadro_w, 
	nr_inscricao_w, 
	nm_contribuinte_w, 
	dt_referencia_w, 
	ie_tipo_declaracao_w, 
	ie_regime_apuracao_w, 
	ie_porte_empresa_w, 
	ie_consolidada_w, 
	ie_centralizada_w, 
	ie_transf_creditos_w, 
	ie_creditos_presu_w, 
	ie_incentivos_fiscais_w, 
	ie_movimento_w, 
	ie_substituto_w, 
	ie_escrita_w, 
	nr_trabalhadores_w, 
	nr_seq_dime_w 
from	fis_dime 
where	cd_estabelecimento	= cd_estabelecimento_p 
and	nr_sequencia		= nr_seq_regra_dime_w;
 
ds_linha_w	:= 	tp_registro_w		|| separador_w || -- campo 01 - tp_registro 
			ds_quadro_w		|| separador_w || -- campo 02 - ds_quadro 
			nr_inscricao_w		|| separador_w || -- campo 03 - número de inscrição do contribuinte 
			nm_contribuinte_w	|| separador_w || -- campo 04 - nome do contribuinte 
			dt_referencia_w		|| separador_w || -- campo 05 - período de referência (mmyyyy) 
			ie_tipo_declaracao_w	|| separador_w || -- campo 06 - tipo de declaração (1 - normal, 2 - encerramento, 3 - saída do regime) 
			ie_regime_apuracao_w	|| separador_w || -- campo 07 - regime de apuração (2 - normal, 3 - estimativa, 7 - bares, 9 produtor) 
			ie_porte_empresa_w	|| separador_w || -- campo 08 - porte da empresa (1 - ME, 2 - EPP, 3 - Normal, 9 - produtor) 
			ie_consolidada_w	|| separador_w || -- campo 09 - apuracao consolidada (1 - não, 2 - estab. consolidador, 3 - estab. consolidado) 
			ie_centralizada_w 	|| separador_w || -- campo 10 - não se aplica 
			ie_transf_creditos_w	|| separador_w || -- campo 11 - transf. créditos (1 - não, 2 - sim, 3 - recebeu, 4 - transferiu e recebeu) 
			ie_creditos_presu_w	|| separador_w || -- campo 12 - créditos presumidos (1 - sim, 2 - não) 
			ie_incentivos_fiscais_w	|| separador_w || -- campo 13 - incentivos fiscais (1 - sim, 2 - não, 3 - desnecessária) 
			ie_movimento_w		|| separador_w || -- campo 14 - movimento (1 - sem mov. sem saldos, 2 - sem mov. com saldo, 3 - com saldo) 
			ie_substituto_w		|| separador_w || -- campo 15 - substituto tributário (1 - sim, 2 - não, 3 - substituto solidário) 
			ie_escrita_w		|| separador_w || -- campo 16 - escrita contábil (1 - sim, estab princ, 2 - nao, 3 - nao, há estab. princ) 
			nr_trabalhadores_w	|| separador_w;	 -- campo 17 - quantidade de trabalhadores 
ds_arquivo_w		:= substr(ds_linha_w,1,4000);
nr_seq_registro_w	:= nr_seq_registro_w + 1;
nr_linha_w		:= nr_linha_w + 1;
 
 
-- insert na tabela do DIME 
insert into w_dime_arquivo(nr_sequencia, 
	dt_atualizacao, 
	nm_usuario, 
	dt_atualizacao_nrec, 
	nm_usuario_nrec, 
	nr_seq_controle_dime, 
	nr_linha, 
	cd_registro, 
	ds_arquivo) 
values (nextval('w_dime_arquivo_seq'), 
	clock_timestamp(), 
	nm_usuario_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	nr_seq_controle_p, 
	nr_linha_w, 
	tp_registro_w, 
	ds_arquivo_w);
 
commit;
 
qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;
 
SELECT * FROM dime_reg_vl_entradas(	nr_seq_controle_p, cd_estabelecimento_p, nm_usuario_p, dt_referencia_p, separador_w, qt_linha_p, nr_sequencia_p) INTO STRICT qt_linha_p, nr_sequencia_p;
			 
SELECT * FROM dime_reg_vl_saidas(	nr_seq_controle_p, cd_estabelecimento_p, nm_usuario_p, dt_referencia_p, separador_w, qt_linha_p, nr_sequencia_p) INTO STRICT qt_linha_p, nr_sequencia_p;
			 
SELECT * FROM dime_reg_resumo_valores(nr_seq_controle_p, cd_estabelecimento_p, nm_usuario_p, dt_referencia_p, separador_w, qt_linha_p, nr_sequencia_p) INTO STRICT qt_linha_p, nr_sequencia_p;
 
SELECT * FROM dime_reg_resumo_debitos(nr_seq_controle_p, cd_estabelecimento_p, nm_usuario_p, dt_referencia_p, separador_w, qt_linha_p, nr_sequencia_p) INTO STRICT qt_linha_p, nr_sequencia_p;
 
SELECT * FROM dime_reg_resumo_creditos(nr_seq_controle_p, cd_estabelecimento_p, nm_usuario_p, dt_referencia_p, separador_w, qt_linha_p, nr_sequencia_p) INTO STRICT qt_linha_p, nr_sequencia_p;			
			 
			 
SELECT * FROM dime_reg_imposto_pagar(	nr_seq_controle_p, cd_estabelecimento_p, nm_usuario_p, dt_referencia_p, separador_w, qt_linha_p, nr_sequencia_p) INTO STRICT qt_linha_p, nr_sequencia_p;
			 
SELECT * FROM dime_reg_credito_especial(	nr_seq_controle_p, cd_estabelecimento_p, nm_usuario_p, dt_referencia_p, separador_w, nr_seq_dime_w, qt_linha_p, nr_sequencia_p) INTO STRICT qt_linha_p, nr_sequencia_p;
			 
SELECT * FROM dime_reg_entradas_uf(	nr_seq_controle_p, cd_estabelecimento_p, nm_usuario_p, dt_referencia_p, separador_w, qt_linha_p, nr_sequencia_p) INTO STRICT qt_linha_p, nr_sequencia_p;
			 
SELECT * FROM dime_reg_saidas_uf(	nr_seq_controle_p, cd_estabelecimento_p, nm_usuario_p, dt_referencia_p, separador_w, qt_linha_p, nr_sequencia_p) INTO STRICT qt_linha_p, nr_sequencia_p;
			 
SELECT * FROM dime_reg_inventario(	nr_seq_controle_p, cd_estabelecimento_p, nm_usuario_p, dt_referencia_p, separador_w, qt_linha_p, nr_sequencia_p) INTO STRICT qt_linha_p, nr_sequencia_p;
			 
SELECT * FROM dime_reg_bp_ativo(	nr_seq_controle_p, cd_estabelecimento_p, nm_usuario_p, dt_referencia_p, separador_w, qt_linha_p, nr_sequencia_p) INTO STRICT qt_linha_p, nr_sequencia_p;
			 
SELECT * FROM dime_reg_bp_passivo(	nr_seq_controle_p, cd_estabelecimento_p, nm_usuario_p, dt_referencia_p, separador_w, qt_linha_p, nr_sequencia_p) INTO STRICT qt_linha_p, nr_sequencia_p;
			 
SELECT * FROM dime_reg_dre(	nr_seq_controle_p, cd_estabelecimento_p, nm_usuario_p, dt_referencia_p, separador_w, qt_linha_p, nr_sequencia_p) INTO STRICT qt_linha_p, nr_sequencia_p;
 
SELECT * FROM dime_reg_bp_despesa(	nr_seq_controle_p, cd_estabelecimento_p, nm_usuario_p, dt_referencia_p, separador_w, qt_linha_p, nr_sequencia_p) INTO STRICT qt_linha_p, nr_sequencia_p;
		 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
