SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE dime_reg_vl_entradas ( nr_seq_controle_p bigint, cd_estabelecimento_p text, nm_usuario_p text, dt_referencia_p timestamp, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


ds_arquivo_w		varchar(4000);
ds_arquivo_compl_w	varchar(4000);
ds_linha_w		varchar(8000);
ds_quadro_w		varchar(2);
nr_linha_w		bigint	:= qt_linha_p;
nr_seq_registro_w	bigint 	:= nr_sequencia_p;
separador_w		varchar(1)	:= ds_separador_p;
tp_registro_w		varchar(2);

cd_cfop_w		integer;
vl_contabil_w		double precision;
vl_base_calculo_w	double precision;
vl_imposto_cred_w	double precision;
vl_isenta_w		double precision	:= 0;
vl_outras_w		double precision;

c01 CURSOR FOR
	SELECT	o.cd_cfop,
		coalesce(sum(a.vl_total_nota),0) vl_total_nota,
		coalesce(sum(obter_dados_trib_item_nf(a.nr_sequencia,'ICMS','BC')),0) vl_base_calculo,
		coalesce(sum(obter_dados_trib_item_nf(a.nr_sequencia,'ICMS','TR')),0) vl_tributo,
		coalesce(sum(obter_dados_trib_item_nf(a.nr_sequencia,'ICMS','O')),0)vl_outras
	FROM natureza_operacao o, nota_fiscal a
LEFT OUTER JOIN modelo_nota_fiscal m ON (a.nr_seq_modelo = m.nr_sequencia)
WHERE a.cd_natureza_operacao	= o.cd_natureza_operacao  and exists (SELECT	1
			from	fis_lote_livro_fiscal	f,
				fis_lote_nota_fiscal	n,
				fis_lote		l
			where	n.nr_seq_lote	= f.nr_seq_lote
			and	a.nr_sequencia	= n.nr_seq_nota_fiscal
			and	f.nr_seq_lote   = l.nr_sequencia
			and	l.ie_tipo_lote	= 'E'
			and	dt_referencia_p between l.dt_inicial and l.dt_final
		) and substr(o.cd_cfop,1,1) in (1,2,3) group by
		o.cd_cfop;


BEGIN

ds_quadro_w	:= '01';
tp_registro_w	:= '22';

open c01;
loop
fetch c01 into
	cd_cfop_w,
	vl_contabil_w,
	vl_base_calculo_w,
	vl_imposto_cred_w,
	vl_outras_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_linha_w	:= 	tp_registro_w								|| separador_w || -- campo 01: tipo de registro [tamanho 02] preencher com "22"
				ds_quadro_w								|| separador_w || -- campo 02:  quadro [tamanho 02] preencher com "01"
				lpad(cd_cfop_w,5,'0')							|| separador_w || -- campo 03: CFOP  [tamanho 02]
				lpad(replace(campo_mascara(coalesce(vl_contabil_w,0),2),'.',''),17,'0')	|| separador_w || -- campo 04:  valor contábil  [tamanho 17]
				lpad(replace(campo_mascara(coalesce(vl_base_calculo_w,0),2),'.',''),17,'0')	|| separador_w || -- campo 05:  valor de base de cálculo  [tamanho 17]
				lpad(replace(campo_mascara(coalesce(vl_imposto_cred_w,0),2),'.',''),17,'0')	|| separador_w || -- campo 06:  valor imposto creditado  [tamanho 17]
				lpad(replace(campo_mascara(coalesce(vl_isenta_w,0),2),'.',''),17,'0')	|| separador_w || -- campo 07: valor operações isentas ou não tributadas  [tamanho 17]
				lpad(replace(campo_mascara(coalesce(vl_outras_w,0),2),'.',''),17,'0')	|| separador_w || -- campo 08: valor da operação ou prestação classificada como outras  [tamanho 17]
				lpad(0,17,'0')								|| separador_w || -- campo 09: Valor correspondente à Base de Cálculo do Imposto Retido
				lpad(0,17,'0')								|| separador_w || -- campo 10: Valor correspondente ao Imposto Retido
				lpad(0,17,'0')								|| separador_w; -- campo 11: Valor correspondente ao Imposto Diferencial de Alíquota
	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w	:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;

	-- insert na tabela do DIME
	insert into w_dime_arquivo(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_controle_dime,
		nr_linha,
		cd_registro,
		ds_arquivo)
	values (nextval('w_dime_arquivo_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_controle_p,
		nr_linha_w,
		tp_registro_w,
		ds_arquivo_w);

	commit;
	end;
end loop;
close c01;

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE dime_reg_vl_entradas ( nr_seq_controle_p bigint, cd_estabelecimento_p text, nm_usuario_p text, dt_referencia_p timestamp, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) FROM PUBLIC;

