	and	f.ie_preco		= 1
	and	coalesce(c.vl_coparticipacao, 0) <> 0
	group by b.nr_sequencia,
		a.ie_tipo_protocolo,
		f.ie_tipo_contratacao,
		e.nr_sequencia,
		f.nr_sequencia,
		a.dt_mes_competencia,
		b.ie_tipo_segurado,
		d.nr_sequencia;

vet_item		c_item%rowtype;


BEGIN

delete	FROM diops_movto_agrup_contrat
where	nr_seq_periodo	= nr_seq_periodo_p;

cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;

select	coalesce(max(ie_status_prov_pagto),'NC'),
	coalesce(max(ie_lote_ajuste_prod),'R'),
	coalesce(max(ie_forma_contab_taxa_pgto),'N')
into STRICT	ie_status_prov_pagto_w,
	ie_lote_ajuste_prod_w,
	ie_forma_contab_taxa_pgto_w
from	pls_parametro_contabil
where	cd_estabelecimento	= cd_estabelecimento_w;

select	trunc(max(dt_periodo_inicial),'yyyy'),
	fim_dia(max(coalesce(dt_periodo_final,fim_mes(add_months(dt_periodo_inicial,2)))))
into STRICT	dt_inicial_w,
	dt_final_w
from	diops_periodo
where	nr_sequencia	= nr_seq_periodo_p;

if (dt_inicial_w >= to_date('01/01/2019', 'dd/mm/yyyy')) then
	ie_2019_ou_posterior_w := 'S';
else
	ie_2019_ou_posterior_w := 'N';
end if;


dt_inicial_ant_w := add_months(dt_inicial_w,-1);
dt_final_ant_w	 := add_months(dt_final_w,-1);

qt_tipo_contratacao_w	:= 2;
qt_tipo_movimento_w	:= 2;

select	coalesce(sum(CASE WHEN ie_prestador_codificacao='P' THEN  1  ELSE 0 END ), 0) qt_prest_pgto
into STRICT	qt_prest_pgto_w
from	pls_esquema_contabil
where	ie_tipo_regra		= 'PP'
and	cd_estabelecimento	= cd_estabelecimento_w
and	trunc(dt_final_w,'month') between dt_inicio_vigencia and coalesce(dt_fim_vigencia, trunc(dt_final_w,'month'));

/*Tratamento com o proposito de a cada geracao do diops, inicializar a tabela com 4 registros, pois ao gerar o XML e necessorio minimo de 4 tags de movimentacao*/

while(qt_tipo_movimento_w > 0) loop
	begin

	insert into diops_movto_agrup_contrat(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						cd_tipo_contratacao,
						cd_tipo_movimento,
						nr_seq_periodo,
						vl_movimento)
					values (nextval('diops_movto_agrup_contrat_seq'),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						qt_tipo_contratacao_w,
						qt_tipo_movimento_w,
						nr_seq_periodo_p,
						0);

	qt_tipo_contratacao_w	:= qt_tipo_contratacao_w - 1;

	if (qt_tipo_contratacao_w = 0) then
		qt_tipo_contratacao_w 	:= 2;
		qt_tipo_movimento_w 	:= qt_tipo_movimento_w - 1;
	end if;

	end;
end loop;

open c_item;
loop
fetch c_item into	
	vet_item;
EXIT WHEN NOT FOUND; /* apply on c_item */
	begin

	if (coalesce(vet_item.nr_seq_contrato, 0) <> 0) then
		
		begin
		
		select	CASE WHEN b.ie_tipo_agrupamento_exclusivo=1 THEN 1  ELSE 2 END
		into STRICT	cd_tipo_movimento_w
		from	pls_contrato_grupo a,
			pls_grupo_contrato b
		where	a.nr_seq_grupo		= b.nr_sequencia
		and 	a.nr_seq_contrato	= vet_item.nr_seq_contrato
		and 	vet_item.dt_mes_competencia between b.dt_inicio_vigencia and b.dt_fim_vigencia;
		
		exception
		when others then
			cd_tipo_movimento_w:= 2;
		end;
		
		if (cd_tipo_movimento_w = 1 and vet_item.ie_benef_remido = 'S') then
			cd_tipo_movimento_w := 2;
			-- Beneficiarios remidos serao gerados como nao pertencentes ao pool de risco, mesmo que estejam em contratos vinculados a esse grupo
		end if;
		
	end if;
	
	insert into diops_movto_agrup_contrat(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						cd_tipo_contratacao,
						cd_tipo_movimento,
						nr_seq_periodo,
						nr_seq_plano,
						nr_seq_conta,
						nr_seq_mensalidade,
						ie_mensalidade_conta,
						vl_movimento)
					values (nextval('diops_movto_agrup_contrat_seq'),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						vet_item.cd_tipo_contratacao,
						cd_tipo_movimento_w,
						nr_seq_periodo_p,
						vet_item.nr_seq_plano,
						vet_item.nr_seq_conta,
						vet_item.nr_seq_mensalidade,
						vet_item.ie_mensalidade_conta,
						vet_item.vl_movimento);
	
	end;
end loop;
close c_item;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE diops_agrupamento_contrato ( nm_usuario_p text, nr_seq_periodo_p bigint) FROM PUBLIC;

