CREATE OR REPLACE PROCEDURE diops_atualizar_dados_envio ( nr_seq_operadora_p bigint, nr_seq_periodo_p bigint, ie_tipo_transacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* IE_TIPO_TRANSACAO_P
	0 - Todos
	1 - Cadastral
	2 - Financeiro
	3 - Complementar Semestral
*/
qt_lancamento_ativo_w		bigint	:= 0;
qt_lancamento_passivo_w		bigint	:= 0;
qt_lancamento_receita_w		bigint	:= 0;
qt_lancamento_despesa_w		bigint	:= 0;


BEGIN

if (ie_tipo_transacao_p in (0,2)) then
	select	count(ds_conta)
	into STRICT	qt_lancamento_ativo_w
	from	diops_fin_mov_ativo
	where	nr_seq_operadora	= nr_seq_operadora_p
	and	nr_seq_periodo		= nr_seq_periodo_p;

	select	count(ds_conta)
	into STRICT	qt_lancamento_passivo_w
	from	diops_fin_mov_passivo
	where	nr_seq_operadora	= nr_seq_operadora_p
	and	nr_seq_periodo		= nr_seq_periodo_p;

	select	count(ds_conta)
	into STRICT	qt_lancamento_receita_w
	from	diops_fin_mov_receita
	where	nr_seq_operadora	= nr_seq_operadora_p
	and	nr_seq_periodo		= nr_seq_periodo_p;

	select	count(ds_conta)
	into STRICT	qt_lancamento_despesa_w
	from	diops_fin_mov_despesa
	where	nr_seq_operadora	= nr_seq_operadora_p
	and	nr_seq_periodo		= nr_seq_periodo_p;

	update	w_diops_fin_balancete
	set	qt_lancamento_ativo	= qt_lancamento_ativo_w,
		qt_lancamento_passivo	= qt_lancamento_passivo_w,
		qt_lancamento_receita	= qt_lancamento_receita_w,
		qt_lancamento_despesa	= qt_lancamento_despesa_w
	where	nr_seq_operadora	= nr_seq_operadora_p
	and	nr_seq_periodo		= nr_seq_periodo_p;

	CALL pls_diops_gravar_historico(nr_seq_periodo_p,5,'','',nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
