CREATE OR REPLACE PROCEDURE diops_cadastral_gerar_telfax ( nr_seq_operadora_p bigint, nr_seq_periodo_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: Gerar as informações de FAX 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ X ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
------------------------------------------------------------------------------------------------------------------- 
Referências: 
	PLS_GERAR_DIOPS_CADASTRAL 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
cd_cgc_w			varchar(14);
cd_representante_w		varchar(10);
cd_repres_rn117_w		varchar(10);
cd_estabelecimento_w		smallint;


BEGIN 
/* Obter dados da operadora */
 
begin 
select	cd_cgc_outorgante, 
	cd_estabelecimento, 
	cd_representante, 
	cd_representante_rn117 
into STRICT	cd_cgc_w, 
	cd_estabelecimento_w, 
	cd_representante_w, 
	cd_repres_rn117_w 
from	pls_outorgante 
where	nr_sequencia	= nr_seq_operadora_p;
exception 
when others then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(243450, 'NR_SEQ_OPERADORA=' || nr_seq_operadora_p);
end;	
 
/* Gravar o número do telefone da Operadora */
 
CALL diops_cadastral_gravar_telfax(nr_seq_operadora_p, nr_seq_periodo_p, '', cd_cgc_w, 'TO', cd_estabelecimento_w, nm_usuario_p);
 
/* Gravar o número do fax da Operadora */
 
CALL diops_cadastral_gravar_telfax(nr_seq_operadora_p, nr_seq_periodo_p, '', cd_cgc_w, 'FO', cd_estabelecimento_w, nm_usuario_p);
 
/* Gravar o número do telefone da Representante */
 
CALL diops_cadastral_gravar_telfax(nr_seq_operadora_p, nr_seq_periodo_p, cd_representante_w, '', 'TR', cd_estabelecimento_w, nm_usuario_p);
 
/* Gravar o número do fax do Representante */
 
CALL diops_cadastral_gravar_telfax(nr_seq_operadora_p, nr_seq_periodo_p, cd_representante_w, '', 'FR', cd_estabelecimento_w, nm_usuario_p);
 
/* Gravar o número do telefone do Representante RN117*/
 
CALL diops_cadastral_gravar_telfax(nr_seq_operadora_p, nr_seq_periodo_p, cd_repres_rn117_w, '', 'TRN', cd_estabelecimento_w, nm_usuario_p);
 
/* Gravar o número do fax do Representante RN117 */
 
CALL diops_cadastral_gravar_telfax(nr_seq_operadora_p, nr_seq_periodo_p, cd_repres_rn117_w, '', 'FRN', cd_estabelecimento_w, nm_usuario_p);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
