SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE diops_cad_gerar_emp_depend ( nr_seq_operadora_p bigint, nr_seq_periodo_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: Gerar as informações cadastrais do DIOPS 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ X ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
------------------------------------------------------------------------------------------------------------------- 
Referências: 
	PLS_GERAR_DIOPS 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
ds_email_um_w			varchar(255);
ds_email_dois_w			varchar(255);
ds_razao_social_w		varchar(80);
ds_tipo_empresa_w		varchar(50);
cd_cgc_w			varchar(14);
ie_tipo_empresa_w		varchar(2);
cd_estabelecimento_w		smallint;
	
C01 CURSOR FOR 
	SELECT	ie_tipo_empresa, 
		cd_cgc, 
		substr(pls_diops_caracteres_nome(obter_razao_social(cd_cgc)),1,80), 
		substr(obter_dados_pf_pj('', cd_cgc, 'M'),1,255) 
	from	pls_empresa_dependente 
	where	nr_seq_operadora	= nr_seq_operadora_p;


BEGIN 
begin /* Obter dados da operadora */
 
select	cd_estabelecimento 
into STRICT	cd_estabelecimento_w 
from	pls_outorgante 
where	nr_sequencia	= nr_seq_operadora_p;
exception 
when others then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(243450, 'NR_SEQ_OPERADORA=' || nr_seq_operadora_p);
end;
 
open C01;
loop 
fetch C01 into	 
	ie_tipo_empresa_w, 
	cd_cgc_w, 
	ds_razao_social_w, 
	ds_email_um_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (ie_tipo_empresa_w = 'F') then 
		ds_tipo_empresa_w	:= 'Filial';
	elsif (ie_tipo_empresa_w = 'S') then 
		ds_tipo_empresa_w	:= 'Sucursal';
	elsif (ie_tipo_empresa_w = 'S') then 
		ds_tipo_empresa_w	:= 'Outra';
	end if;
	insert into w_diops_cad_emp_depend(nr_sequencia, 
		nr_seq_operadora, 
		ie_tipo_empresa, 
		cd_cgc, 
		ds_razao_social, 
		ds_email_um, 
		ds_email_dois, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		ds_tipo_empresa, 
		nr_seq_periodo) 
	values (nextval('w_diops_cad_emp_depend_seq'), 
		nr_seq_operadora_p, 
		ie_tipo_empresa_w, 
		cd_cgc_w, 
		ds_razao_social_w, 
		ds_email_um_w, 
		ds_email_dois_w, 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		ds_tipo_empresa_w, 
		nr_seq_periodo_p);
		 
	CALL diops_cadastral_gravar_end(nr_seq_operadora_p, nr_seq_periodo_p, '', cd_cgc_w, 'E', cd_estabelecimento_w, nm_usuario_p);
	 
	CALL diops_cadastral_gravar_telfax(nr_seq_operadora_p, nr_seq_periodo_p, '', cd_cgc_w, 'TE', cd_estabelecimento_w, nm_usuario_p);
	 
	CALL diops_cadastral_gravar_telfax(nr_seq_operadora_p, nr_seq_periodo_p, '', cd_cgc_w, 'FE', cd_estabelecimento_w, nm_usuario_p);	
	end;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
