CREATE OR REPLACE PROCEDURE diops_cad_gerar_munatuacao ( nr_seq_operadora_p bigint, nr_seq_periodo_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Gerar as informações de Municípios de atuação
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
	PLS_GERAR_DIOPS_CADASTRAL
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_municipio_ibge_w		varchar(6);
ie_todos_municipios_w		varchar(1);
sg_uf_municipio_w		varchar(2);
qt_uf_municipios_w		integer	:= 0;

C01 CURSOR FOR
	SELECT	sg_uf_municipio,
		cd_municipio_ibge,
		ie_todos_municipios
	from	pls_regiao_atuacao
	where	nr_seq_operadora	= nr_seq_operadora_p;


BEGIN
open C01;
loop
fetch C01 into
	sg_uf_municipio_w,
	cd_municipio_ibge_w,
	ie_todos_municipios_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_todos_municipios_w	= 'S') then
		select	count(1)
		into STRICT	qt_uf_municipios_w
		from	w_diops_cad_mun_atuacao
		where	nr_seq_operadora	= nr_seq_operadora_p
		and	sg_uf_municipio		= sg_uf_municipio_w  LIMIT 1;

		if (qt_uf_municipios_w > 0) then
			delete	from w_diops_cad_mun_atuacao
			where	nr_seq_operadora	= nr_seq_operadora_p
			and	sg_uf_municipio		= sg_uf_municipio_w;
		end if;
	end if;

	insert into w_diops_cad_mun_atuacao(nr_sequencia,
		nr_seq_operadora,
		sg_uf_municipio,
		cd_municipio_ibge,
		ie_todos_municipios,
		dt_atualizacao,
		nm_usuario,
		nr_seq_periodo)
	values (nextval('w_diops_cad_mun_atuacao_seq'),
		nr_seq_operadora_p,
		sg_uf_municipio_w,
		cd_municipio_ibge_w,
		ie_todos_municipios_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_periodo_p);
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
