CREATE OR REPLACE PROCEDURE diops_cad_gerar_resp_tecnico ( nr_seq_operadora_p bigint, nr_seq_periodo_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
nr_crm_resp_tecnico_w	w_diops_cad_resp_tecnico.nr_crm_resp_tecnico%type;
dt_periodo_final_w	timestamp;

c_responsavel CURSOR FOR 
	SELECT	a.cd_pessoa_fisica, 
		substr(obter_dados_pf(a.cd_pessoa_fisica,'CPF'),1,11) nr_cpf, 
		a.dt_vigencia_inicial, 
		a.dt_vigencia_final, 
		a.ie_tipo_responsavel 
	from	pls_responsavel_tecnico a 
	where	coalesce(a.nr_seq_operadora,nr_seq_operadora_p) = nr_seq_operadora_p 
	and	trunc(dt_periodo_final_w,'dd') between a.dt_vigencia_inicial and coalesce(a.dt_vigencia_final,dt_periodo_final_w);

c_responsavel_w	c_responsavel%rowtype;


BEGIN 
 
select	fim_dia(dt_periodo_final) 
into STRICT	dt_periodo_final_w 
from	diops_periodo 
where	nr_sequencia	= nr_seq_periodo_p;
 
open c_responsavel;
loop 
fetch c_responsavel into	 
	c_responsavel_w;
EXIT WHEN NOT FOUND; /* apply on c_responsavel */
	begin 
	 
	begin 
	nr_crm_resp_tecnico_w	:= substr(obter_crm_medico(c_responsavel_w.cd_pessoa_fisica),1,20);
	exception 
	when others then 
		nr_crm_resp_tecnico_w	:= null;
	end;
		 
	insert into w_diops_cad_resp_tecnico(nr_sequencia, 
	    nm_usuario, 
	    dt_atualizacao, 
	    nr_seq_operadora, 
	    cd_pessoa_fisica, 
	    nr_cpf, 
	    nr_crm_resp_tecnico, 
	    dt_vigencia_inicial, 
	    dt_vigencia_final, 
	    ie_tipo_responsavel, 
	    nr_seq_periodo) 
	values (nextval('w_diops_cad_resp_tecnico_seq'), 
		nm_usuario_p, 
		clock_timestamp(), 
		nr_seq_operadora_p, 
		c_responsavel_w.cd_pessoa_fisica, 
		c_responsavel_w.nr_cpf, 
		nr_crm_resp_tecnico_w, 
		c_responsavel_w.dt_vigencia_inicial, 
		c_responsavel_w.dt_vigencia_final, 
		c_responsavel_w.ie_tipo_responsavel, 
		nr_seq_periodo_p);
	 
	end;
end loop;
close c_responsavel;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
