CREATE OR REPLACE PROCEDURE diops_cad_gravar_responsavel ( nr_seq_operadora_p bigint, nr_seq_periodo_p bigint, cd_pessoa_fisica_p text, cd_pessoa_juridica_p text, ie_tipo_responsavel_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Gerar as informações cadastrais do responsável
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
	ie_tipo_responsavel_p
		C - Contabilidade
		T - Atuária
		A - Auditoria
-------------------------------------------------------------------------------------------------------------------
Referências:
	DIOPS_CAD_GERAR_RESPONSAVEL
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nm_responsavel_w		varchar(80);
nr_registro_pls_w		varchar(20);
cd_pessoa_juridica_w		varchar(14);
nr_cpf_w			varchar(11);
cd_pessoa_fisica_w		varchar(10);


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	cd_pessoa_fisica_w	:= cd_pessoa_fisica_p;
	/* Buscar dados da pessoa fisica */

	select	nr_cpf,
		nm_pessoa_fisica,
		nr_registro_pls
	into STRICT	nr_cpf_w,
		nm_responsavel_w,
		nr_registro_pls_w
	from	pessoa_fisica
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

	cd_pessoa_juridica_w	:= '';
elsif (cd_pessoa_juridica_p IS NOT NULL AND cd_pessoa_juridica_p::text <> '') then
	cd_pessoa_juridica_w	:= cd_pessoa_juridica_p;
	nr_cpf_w		:= '';

	/* Buscar dados da pessoa jurídica */

	select	ds_razao_social,
		nr_registro_pls
	into STRICT	nm_responsavel_w,
		nr_registro_pls_w
	from	pessoa_juridica
	where	cd_cgc	= cd_pessoa_juridica_p;

	cd_pessoa_fisica_w	:= '';
end if;

insert into w_diops_cad_responsavel(nr_sequencia,
	nr_seq_operadora,
	ie_tipo_responsavel,
	cd_pessoa_fisica,
	nr_cpf,
	cd_cgc,
	nm_responsavel,
	nr_num_registro,
	dt_atualizacao,
	nm_usuario,
	nr_seq_periodo)
values (nextval('w_diops_cad_responsavel_seq'),
	nr_seq_operadora_p,
	ie_tipo_responsavel_p,
	cd_pessoa_fisica_w,
	nr_cpf_w,
	cd_pessoa_juridica_w,
	nm_responsavel_w,
	nr_registro_pls_w,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_periodo_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
