CREATE OR REPLACE PROCEDURE diops_consistir_administrador ( nr_seq_periodo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nm_administrador_w		varchar(60);
nr_cpf_w			varchar(11);
cd_pessoa_fisica_w		varchar(10);

C01 CURSOR FOR
	SELECT	nm_administrador,
		nr_cpf,
		cd_pessoa_fisica
	from	w_diops_cad_administrador
	where	nr_seq_periodo	= nr_seq_periodo_p;


BEGIN
open C01;
loop
fetch C01 into
	nm_administrador_w,
	nr_cpf_w,
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	/* 2 - Pessoa física sem a informação do CPF */

	if (coalesce(nr_cpf_w::text, '') = '') then
		CALL diops_gravar_inconsistencia(	nr_seq_periodo_p,
						2,
						'Administrador: ' || nm_administrador_w,
						cd_pessoa_fisica_w,
						nm_usuario_p,
						cd_estabelecimento_p);
	end if;
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
