CREATE OR REPLACE PROCEDURE diops_consistir_represenrn117 ( nr_seq_periodo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nm_pessoa_fisica_w		varchar(60);
nr_identidade_w			varchar(15);
cd_repre_rn117_w		varchar(10);
ds_orgao_emissor_ci_w		pessoa_fisica.ds_orgao_emissor_ci%type;
dt_emissao_ci_w			timestamp;


BEGIN
select	coalesce(max(cd_repre_rn117), '')
into STRICT	cd_repre_rn117_w
from	w_diops_cad_identif
where	nr_seq_periodo	= nr_seq_periodo_p;

select	nm_pessoa_fisica,
	nr_identidade,
	dt_emissao_ci,
	ds_orgao_emissor_ci
into STRICT	nm_pessoa_fisica_w,
	nr_identidade_w,
	dt_emissao_ci_w,
	ds_orgao_emissor_ci_w
from	pessoa_fisica
where	cd_pessoa_fisica	= cd_repre_rn117_w;

/* 3 - Pessoa física sem a informação do RG */

if (coalesce(nr_identidade_w::text, '') = '') then
	CALL diops_gravar_inconsistencia(	nr_seq_periodo_p,
					3,
					cd_repre_rn117_w || ' - ' || wheb_mensagem_pck.get_texto(298464) || ' RN117 - ' || nm_pessoa_fisica_w, -- Representante
					cd_repre_rn117_w,
					nm_usuario_p,
					cd_estabelecimento_p);
end if;

/* 4 - Pessoa física sem a informação da data de expedição do RG */

if (coalesce(dt_emissao_ci_w::text, '') = '') then
	CALL diops_gravar_inconsistencia(	nr_seq_periodo_p,
					4,
					cd_repre_rn117_w || ' - ' || wheb_mensagem_pck.get_texto(298464) || ' RN117 - ' || nm_pessoa_fisica_w, -- Representante
					cd_repre_rn117_w,
					nm_usuario_p,
					cd_estabelecimento_p);
end if;

/* 5 - Pessoa física sem a informação do órgão expedidor do RG */

if (coalesce(ds_orgao_emissor_ci_w::text, '') = '') then
	CALL diops_gravar_inconsistencia(	nr_seq_periodo_p,
					5,
					cd_repre_rn117_w || ' - ' || wheb_mensagem_pck.get_texto(298464) || ' RN117 - ' || nm_pessoa_fisica_w, -- Representante
					cd_repre_rn117_w,
					nm_usuario_p,
					cd_estabelecimento_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
