SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE diops_consistir_representante ( nr_seq_periodo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nm_representante_w		varchar(60);
ds_logradouro_w			varchar(40);
ds_bairro_w			varchar(40);
ds_complemento_w		varchar(20);
cd_cep_w			varchar(15);
cd_representante_w		varchar(14);
nr_logradouro_w			varchar(10);
cd_municipio_ibge_w		varchar(6);
ds_detalhe_w			varchar(255);


BEGIN
select	coalesce(cd_representante, ''),
	nm_representante
into STRICT	cd_representante_w,
	nm_representante_w
from	w_diops_cad_identif
where	nr_seq_periodo	= nr_seq_periodo_p;

select	ds_logradouro,
	nr_logradouro,
	ds_complemento,
	cd_cep,
	ds_bairro,
	cd_municipio_ibge
into STRICT	ds_logradouro_w,
	nr_logradouro_w,
	ds_complemento_w,
	cd_cep_w,
	ds_bairro_w,
	cd_municipio_ibge_w
from	w_diops_cad_endereco
where	ie_tipo_endereco	= 'R'
and	nr_seq_periodo		= nr_seq_periodo_p;

/* 11 - Complemento do representante n√£o informado */

if (coalesce(ds_complemento_w::text, '') = '') then

	ds_detalhe_w	:= wheb_mensagem_pck.get_texto(299551,'CD_REPRESENTANTE=' || cd_representante_w ||
								';NM_REPRESENTANTE=' || nm_representante_w);

	CALL diops_gravar_inconsistencia(	nr_seq_periodo_p,
					11,
					ds_detalhe_w,
					cd_representante_w,
					nm_usuario_p,
					cd_estabelecimento_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
