CREATE OR REPLACE PROCEDURE diops_fin_gerar_ativo_vinc ( nr_seq_operadora_p bigint, nr_seq_transacao_p bigint, nr_seq_periodo_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_periodo_final_w	timestamp;


BEGIN

begin
select	coalesce(dt_periodo_final, '')
into STRICT	dt_periodo_final_w
from	diops_periodo
where	nr_sequencia	= nr_seq_periodo_p;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(183308, 'NR_SEQ_OPERADORA=' || nr_seq_operadora_p || ';' || 'NR_SEQ_PERIODO=' || nr_seq_periodo_p);
end;

CALL diops_gerar_ativo_imobiliario(nr_seq_operadora_p, nr_seq_transacao_p, nr_seq_periodo_p, nm_usuario_p);

if (dt_periodo_final_w < pkg_date_utils.get_dateTime(2018, 01, 01)) then
	CALL diops_gerar_ativo_investimento(nr_seq_operadora_p, nr_seq_transacao_p, nr_seq_periodo_p, nm_usuario_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
