SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE diops_gerar_ativo_imobiliario ( nr_seq_operadora_p bigint, nr_seq_transacao_p bigint, nr_seq_periodo_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_periodo_inicial_w	timestamp;
dt_periodo_final_w	timestamp;
dt_aquisicao_w		timestamp;
dt_venda_w		timestamp;
dt_avaliacao_w		timestamp;
nr_endereco_w		integer;
qt_area_w		integer;
nr_seq_ativo_imob_w	bigint;
nr_seq_ativo_imob_ww	bigint;
nr_rgi_w		bigint;
nr_seq_tipo_bem_w	bigint;
vl_preco_unitario_w	double precision;
vl_contabil_w		double precision;
ie_rede_propria_w	varchar(1);
cd_tipo_item_w		varchar(6);
cd_municipio_ibge_w	varchar(6);
cd_cgc_cartorio_w	varchar(14);
cd_cep_w		varchar(15);
ds_complemento_w	varchar(40);
ds_bairro_w		varchar(40);
ds_razao_social_w	varchar(80);
ds_endereco_w		varchar(100);
sg_estado_w		pessoa_juridica.sg_estado%type;
ie_assistencial_w	pls_ativo_imobiliario.ie_assistencial%type;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_rgi,
		nr_seq_tipo_bem,
		cd_cgc_cartorio,
		qt_area,
		dt_aquisicao,
		dt_venda,
		dt_avaliacao,
		coalesce(ie_rede_propria,'N'),
		vl_preco_unitario,
		vl_contabil,
		coalesce(ie_assistencial,'N')
	from	pls_ativo_imobiliario
	where	nr_seq_operadora	= nr_seq_operadora_p
	and	ie_situacao		= 'A';


BEGIN

/* Obter o período trimestral do DIOPS */

begin
select	coalesce(dt_periodo_inicial,''),
	coalesce(dt_periodo_final, '')
into STRICT	dt_periodo_inicial_w,
	dt_periodo_final_w
from	diops_periodo
where	nr_sequencia	= nr_seq_periodo_p;
exception
	when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(267413,
						'NR_SEQ_OPERADORA_P=' || nr_seq_operadora_p||';'||
						'NR_SEQ_PERIODO_P=' || nr_seq_periodo_p);
end;

open C01;
loop
fetch C01 into
	nr_seq_ativo_imob_w,
	nr_rgi_w,
	nr_seq_tipo_bem_w,
	cd_cgc_cartorio_w,
	qt_area_w,
	dt_aquisicao_w,
	dt_venda_w,
	dt_avaliacao_w,
	ie_rede_propria_w,
	vl_preco_unitario_w,
	vl_contabil_w,
	ie_assistencial_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	/* Obter o código do tipo do item imobiliario */

	select	coalesce(max(cd_tipo),'')
	into STRICT	cd_tipo_item_w
	from	pls_tipo_bem_imobiliario
	where	nr_sequencia	= nr_seq_tipo_bem_w;

	/* Obter o nome do cartório do bem imobiliario */

	select	coalesce(max(ds_razao_social),'')
	into STRICT	ds_razao_social_w
	from	pessoa_juridica
	where	cd_cgc	= cd_cgc_cartorio_w;

	insert into w_diops_fin_ativo_imob(nr_sequencia,
		nr_seq_operadora,
		nr_seq_transacao,
		nr_seq_periodo,
		nr_rgi,
		nr_seq_tipo_bem,
		cd_tipo_bem,
		cd_cgc_cartorio,
		nm_cartorio,
		qt_area,
		dt_atualizacao,
		nm_usuario,
		dt_aquisicao,
		dt_venda,
		dt_avaliacao,
		ie_rede_propria,
		vl_preco_unitario,
		vl_contabil,
		nr_seq_ativo_imob,
		ie_assistencial)
	values (	nextval('w_diops_fin_ativo_imob_seq'),
		nr_seq_operadora_p,
		nr_seq_transacao_p,
		nr_seq_periodo_p,
		nr_rgi_w,
		nr_seq_tipo_bem_w,
		cd_tipo_item_w,
		cd_cgc_cartorio_w,
		ds_razao_social_w,
		qt_area_w,
		clock_timestamp(),
		nm_usuario_p,
		dt_aquisicao_w,
		dt_venda_w,
		dt_avaliacao_w,
		ie_rede_propria_w,
		vl_preco_unitario_w,
		vl_contabil_w,
		nr_seq_ativo_imob_w,
		ie_assistencial_w)
	returning nr_sequencia into nr_seq_ativo_imob_ww;

	/* Obter dados do endereço do imóvel */

	begin
	select	ds_endereco,
		nr_endereco,
		ds_complemento,
		ds_bairro,
		cd_municipio_ibge,
		sg_estado,
		cd_cep
	into STRICT	ds_endereco_w,
		nr_endereco_w,
		ds_complemento_w,
		ds_bairro_w,
		cd_municipio_ibge_w,
		sg_estado_w,
		cd_cep_w
	from	pls_endereco_imovel
	where	nr_seq_ativo_imob	= nr_seq_ativo_imob_w;

	insert into w_diops_endereco_ativo(nr_sequencia,
		nr_seq_operadora,
		nr_seq_transacao,
		nr_seq_periodo,
		ds_logradouro,
		nr_logradouro,
		ds_complemento,
		ds_bairro,
		cd_municipio_ibge,
		sg_uf,
		dt_atualizacao,
		nm_usuario,
		cd_cep,
		nr_seq_ativo_imob)
	values (	nextval('w_diops_endereco_ativo_seq'),
		nr_seq_operadora_p,
		nr_seq_transacao_p,
		nr_seq_periodo_p,
		ds_endereco_w,
		nr_endereco_w,
		ds_complemento_w,
		ds_bairro_w,
		cd_municipio_ibge_w,
		sg_estado_w,
		clock_timestamp(),
		nm_usuario_p,
		cd_cep_w,
		nr_seq_ativo_imob_ww);
	exception
		when others then
		ds_endereco_w	:= '';
	end;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE diops_gerar_ativo_imobiliario ( nr_seq_operadora_p bigint, nr_seq_transacao_p bigint, nr_seq_periodo_p bigint, nm_usuario_p text) FROM PUBLIC;

