SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE diops_gerar_ativo_investimento ( nr_seq_operadora_p bigint, nr_seq_transacao_p bigint, nr_seq_periodo_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_tipo_custodia_w	varchar(20);
cd_ativo_w		varchar(10);
cd_tipo_item_w		varchar(6);
vl_preco_unitario_w	double precision;
vl_contabil_w		double precision;
vl_saldo_final_w	double precision;
qt_investimento_w	double precision;
qt_invest_conta_w	bigint;
nr_seq_ativo_invest_w	bigint;
nr_seq_tipo_bem_w	bigint;
dt_periodo_inicial_w	timestamp;
dt_periodo_final_w	timestamp;
dt_emissao_w		timestamp;
dt_vencimento_w		timestamp;
ds_emissor_w		pls_ativo_investimento.ds_emissor%type;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;

C01 CURSOR FOR
	SELECT	substr(obter_valor_dominio(2192, ie_tipo_custodia),1,20),
		cd_ativo,
		dt_emissao,
		dt_vencimento,
		nr_seq_tipo_bem,
		qt_investimento,
		vl_preco_unitario,
		vl_contabil,
		nr_sequencia,
		ds_emissor
	from	pls_ativo_investimento
	where	nr_seq_operadora	= nr_seq_operadora_p
	and	ie_situacao		= 'A';


BEGIN

/* Obter o período trimestral do DIOPS */

begin
select	coalesce(dt_periodo_inicial,''),
	coalesce(dt_periodo_final, ''),
	cd_estabelecimento
into STRICT	dt_periodo_inicial_w,
	dt_periodo_final_w,
	cd_estabelecimento_w
from	diops_periodo
where	nr_sequencia	= nr_seq_periodo_p;
exception
	when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(215583, 'NR_SEQ_OPERADORA_P=' || nr_seq_operadora_p || ';' || 'NR_SEQ_PERIODO_P=' || nr_seq_periodo_p);
end;

open C01;
loop
fetch C01 into
	ds_tipo_custodia_w,
	cd_ativo_w,
	dt_emissao_w,
	dt_vencimento_w,
	nr_seq_tipo_bem_w,
	qt_investimento_w,
	vl_preco_unitario_w,
	vl_contabil_w,
	nr_seq_ativo_invest_w,
	ds_emissor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(1)
	into STRICT	qt_invest_conta_w
	from	pls_ativo_invest_conta x
	where	x.nr_seq_ativo_invest = nr_seq_ativo_invest_w  LIMIT 1;

	if (qt_invest_conta_w > 0) then
		select	coalesce(sum(a.vl_saldo_final),0)
		into STRICT	vl_saldo_final_w
		from	diops_fin_mov_ativo a
		where	a.ds_conta IN ( SELECT	x.ds_conta
					from	pls_ativo_invest_conta x
					where	x.nr_seq_ativo_invest = nr_seq_ativo_invest_w)
		and	a.nr_seq_periodo = nr_seq_periodo_p
		and	a.nr_seq_operadora = nr_seq_operadora_p;

		vl_preco_unitario_w 	:= vl_saldo_final_w;
		vl_contabil_w		:= vl_saldo_final_w;
	end if;

	/* Obter o código do tipo do item investimento */

	select	coalesce(max(cd_tipo),'')
	into STRICT	cd_tipo_item_w
	from	pls_tipo_bem_investimento
	where	nr_sequencia	= nr_seq_tipo_bem_w;


	insert into w_diops_fin_ativo_invest(nr_sequencia,
		nr_seq_operadora,
		nr_seq_transacao,
		nr_seq_periodo,
		ds_tipo_custodia,
		cd_ativo,
		dt_emissao,
		dt_vencimento,
		nr_seq_tipo_bem,
		cd_tipo_bem,
		dt_atualizacao,
		nm_usuario,
		qt_investimento,
		vl_preco_unitario,
		vl_contabil,
		ds_emissor)
	values (	nextval('w_diops_fin_ativo_invest_seq'),
		nr_seq_operadora_p,
		nr_seq_transacao_p,
		nr_seq_periodo_p,
		ds_tipo_custodia_w,
		cd_ativo_w,
		dt_emissao_w,
		dt_vencimento_w,
		nr_seq_tipo_bem_w,
		cd_tipo_item_w,
		clock_timestamp(),
		nm_usuario_p,
		qt_investimento_w,
		vl_preco_unitario_w,
		vl_contabil_w,
		ds_emissor_w);
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE diops_gerar_ativo_investimento ( nr_seq_operadora_p bigint, nr_seq_transacao_p bigint, nr_seq_periodo_p bigint, nm_usuario_p text) FROM PUBLIC;

