CREATE OR REPLACE PROCEDURE diops_gravar_inconsistencia ( nr_seq_periodo_p bigint, cd_inconsistencia_p bigint, ds_detalhe_p text, cd_pessoa_fisica_p text, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_movimento_p bigint default null) AS $body$
DECLARE


nr_seq_inconsistencia_w		bigint;
nr_sequencia_w			bigint;
ds_acao_usuario_w		varchar(4000);


BEGIN

select	nr_sequencia,
	coalesce(ds_acao_usuario,'')
into STRICT	nr_seq_inconsistencia_w,
	ds_acao_usuario_w
from	diops_inconsistencia
where	cd_inconsistencia	= cd_inconsistencia_p;

select	coalesce(max(nr_sequencia),0) + 1
into STRICT	nr_sequencia_w
from	diops_periodo_inconsist
where	nr_seq_periodo	= nr_seq_periodo_p;

insert into diops_periodo_inconsist(nr_seq_periodo,
	nr_sequencia,
	cd_estabelecimento,
	nr_seq_inconsistencia,
	dt_atualizacao,
	nm_usuario,
	ds_acao_usuario,
	ds_detalhe,
	cd_pessoa_fisica,
	nr_seq_movimento)
values (nr_seq_periodo_p,
	nr_sequencia_w,
	cd_estabelecimento_p,
	nr_seq_inconsistencia_w,
	clock_timestamp(),
	nm_usuario_p,
	ds_acao_usuario_w,
	ds_detalhe_p,
	cd_pessoa_fisica_p,
	nr_seq_movimento_p);

commit;

end;
$body$
