SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE dirf_grava_rio ( nr_seq_lote_dirf_p bigint, nm_usuario_p text, nr_seq_apres_p INOUT bigint, cd_darf_p text, cd_estabelecimento_p bigint, cd_pessoa_fisica_p text) is vl_rendimento_w varchar(20) AS $body$
BEGIN
return;
end;

begin
vl_rendimento_w			:= 0;
ds_arquivo_w			:= '';
ds_operacao_nf_w			:= '';
separador_w			:= '|';

select	substr(obter_dados_operacao_nota((max(a.cd_operacao_nf)),1),1,60) ds_operacao_nf,
	coalesce(sum(a.vl_rendimento),0) mes0
into STRICT	ds_operacao_nf_w,
	vl_rendimento_w
from	dirf_lote_mensal	e,
	dirf_agrupar_lote	d,
	dirf_lote		c,
	titulo_pagar	b,
	dirf_titulo_pagar	a
where	1 = 1
and	e.nr_sequencia		= a.nr_seq_lote_dirf
and	e.nr_sequencia		= d.nr_seq_lote_mensal
and	c.nr_sequencia		= d.nr_seq_lote_anual
and	a.nr_titulo			= b.nr_titulo
and	c.nr_sequencia		= nr_seq_lote_dirf_p
and	a.ie_registro		= 'RIO'
and	coalesce(a.cd_pessoa_fisica, b.cd_pessoa_fisica)	= cd_pessoa_fisica_p
and	a.cd_darf = cd_darf_p;

if (abs(vl_rendimento_w) > 0) then
	begin

	ds_arquivo_w := 'RIO'	|| separador_w || campo_dirf(vl_rendimento_w)
				|| separador_w || ds_operacao_nf_w || separador_w;

	nr_seq_apres_p := nr_seq_apres_p + 1;

	insert 	into w_dirf_arquivo(nr_sequencia,
		nm_usuario,
		nm_usuario_nrec,
		dt_atualizacao,
		dt_atualizacao_nrec,
		ds_arquivo,
		nr_seq_apresentacao,
		nr_seq_registro,
		cd_darf)
	values (nextval('w_dirf_arquivo_seq'),
		nm_usuario_p,
		nm_usuario_p,
		clock_timestamp(),
		clock_timestamp(),
		ds_arquivo_w,
		to_char(nr_seq_apres_p),
		0,
		cd_darf_p);

	end;
end if;

commit;

nr_seq_apres_p	:= nr_seq_apres_p + 1;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
