SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE dirf_registro_dirf ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE

			      
separador_w	varchar(1) := '|';
ds_arquivo_w	varchar(2000);



BEGIN

select 	'Dirf' || separador_w ||
	lpad(to_char(clock_timestamp(),'yyyy'),4,' ') || separador_w || --ano calendario
	lpad(to_char(a.dt_lote,'yyyy'),4,' ') || separador_w || --ano calendario
	CASE WHEN lpad(coalesce(substr(upper(a.ie_retificadora),1,1),' '),1,' ')='O' THEN 'N'  ELSE 'S' END  || separador_w || -- retificadora ou original
	separador_w || -- n√∫mero recibo
	coalesce(a.ds_identificador,'M1LB5V2') || separador_w
into STRICT	ds_arquivo_w	
from	dirf_lote a
where	a.nr_sequencia = nr_seq_lote_p;

insert 	into w_dirf_arquivo(nr_sequencia,
			     nm_usuario,
			     nm_usuario_nrec,
			     dt_atualizacao,
			     dt_atualizacao_nrec,
			     ds_arquivo,
			     nr_seq_apresentacao,
			     nr_seq_registro)
values (nextval('w_dirf_arquivo_seq'),
			     nm_usuario_p,
			     nm_usuario_p,
			     clock_timestamp(),
			     clock_timestamp(),
			     substr(ds_arquivo_w,1,4000),
			     '1',
			     0);
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
