CREATE OR REPLACE PROCEDURE disint_gerar_inventario ( nr_seq_inventario_p INOUT bigint, cd_local_estoque_p bigint, ie_consignado_p text, cd_material_p bigint, qt_contagem_p bigint, cd_fornecedor_p text, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE



nr_seq_inventario_w		bigint	:= coalesce(nr_seq_inventario_p,0);
cd_local_estoque_w		integer  	:= cd_local_estoque_p;
ie_contagem_atual_w		varchar(15)	:= '1';
ie_contagem_inventario_w	varchar(15);
nr_seq_item_inv_w		bigint;
ds_erro_w			varchar(2000);
qt_existe_item_inv_w		bigint;


BEGIN

if (nr_seq_inventario_w = 0) then
	begin

	select 	nextval('inventario_seq')
	into STRICT	nr_seq_inventario_w
	;

	begin
	insert into inventario(nr_sequencia,
		cd_estabelecimento,
		dt_atualizacao,
		nm_usuario,
		cd_local_estoque,
		dt_bloqueio,
		nm_usuario_bloqueio,
		qt_max_contagem,
		ie_novo_item_barra,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		qt_max_definir,
		ie_consignado,
		ie_contagem_atual,
		ie_tipo_contagem,
		dt_inicio,
		ds_observacao)
	values (nr_seq_inventario_w,
		cd_estabelecimento_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_local_estoque_w,
		clock_timestamp(), -- Bloqueio
		nm_usuario_p,
		1,
		'N',
		clock_timestamp(),
		nm_usuario_p,
		1,
		ie_consignado_p,
		'1',
		'I',
		clock_timestamp(),
		'Invet√°rio realizado a partir do PYXIS');
	exception when others then
		ds_erro_w := substr(sqlerrm,1,2000);
	end;

	ie_contagem_atual_w := '1';
	end;
end if;

if (coalesce(ds_erro_w,'X') = 'X') then
	begin
	select	count(*)
	into STRICT	qt_existe_item_inv_w
	from	inventario_material
	where	nr_seq_inventario = nr_seq_inventario_w
	and	cd_material = cd_material_p;

	if (qt_existe_item_inv_w = 0) then
		begin
		select	nextval('inventario_material_seq')
		into STRICT	nr_seq_item_inv_w
		;

		begin
		insert into inventario_material(nr_sequencia,
			nr_seq_inventario,
			dt_atualizacao,
			nm_usuario,
			cd_material,
			qt_contagem,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_fornecedor,
			ie_status_inventario,
			nr_seq_ordem,
			ie_contagem_atual,
			dt_contagem,
			nm_usuario_contagem)
		values (nr_seq_item_inv_w,
			nr_seq_inventario_w,
			clock_timestamp(),
			nm_usuario_p,
			cd_material_p,
			qt_contagem_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_fornecedor_p,
			'P',
			1,
			'1',
			clock_timestamp(),
			nm_usuario_p);
		exception when others then
			ds_erro_w := substr(sqlerrm,1,2000);
		end;
		end;
	end if;
	end;
end if;

nr_seq_inventario_p	:= nr_seq_inventario_w;
ds_erro_p 		:= ds_erro_w;

commit;

end;
