CREATE OR REPLACE PROCEDURE dispensar_sanprodu ( nr_seq_array text,nr_seq_transfusao_p bigint, nm_usuario_p text) AS $body$
BEGIN
	update
		san_producao 
	set	
		DT_DISPENSACAO  = clock_timestamp(),
		NM_USUARIO_DISPENSACAO     = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		NM_USUARIO= nm_usuario_p
	where
		NR_SEQ_TRANSFUSAO = nr_seq_transfusao_p 
		and NR_SEQUENCIA in (SELECT NR_REGISTRO from table(lista_pck.obter_lista(nr_seq_array)) );
commit;

end;
$body$
