CREATE OR REPLACE PROCEDURE disp_juros_multa ( NR_TITULO_P bigint, DT_LIQUIDACAO_P timestamp, IE_JUROS_MULTA_P text, VL_MOVIMENTO_P bigint ) AS $body$
DECLARE


 VL_SALDO_JUROS_W   titulo_receber.VL_SALDO_JUROS%type;
 VL_SALDO_MULTA_W   titulo_receber.VL_SALDO_MULTA%type;
 VL_SALDO_TITULO_W  titulo_receber.VL_SALDO_TITULO%type;


BEGIN

select VL_SALDO_JUROS, VL_SALDO_MULTA, VL_SALDO_TITULO
into STRICT VL_SALDO_JUROS_W,
     VL_SALDO_MULTA_W,
     VL_SALDO_TITULO_W
from titulo_receber
where nr_titulo = NR_TITULO_P;

IF ((VL_SALDO_TITULO_W = 0) and (VL_SALDO_MULTA_W = 0) and (VL_SALDO_JUROS_W = 0)) THEN

	UPDATE TITULO_RECEBER
	SET DT_LIQUIDACAO = DT_LIQUIDACAO_P,
		IE_SITUACAO = 2
	WHERE NR_TITULO = NR_TITULO_P;

ELSIF (IE_JUROS_MULTA_P = 'J')THEN

	UPDATE TITULO_RECEBER
	SET VL_SALDO_JUROS = (VL_SALDO_JUROS_W - VL_MOVIMENTO_P)
	WHERE NR_TITULO = NR_TITULO_P;

ELSE

	UPDATE TITULO_RECEBER
	SET VL_SALDO_MULTA = (VL_SALDO_MULTA_W - VL_MOVIMENTO_P)
	WHERE NR_TITULO = NR_TITULO_P;

END IF;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
