SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE dis_atualizar_valores_item (nr_sequencia_item_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_material_w	double precision;
vl_unitario_material_w	double precision;
vl_liquido_material_w	double precision;
vl_total_material_w	double precision;
vl_desconto_w	double precision;
pr_desconto_w	double precision;
pr_tributo_w	double precision;
vl_tributo_w	double precision;
vl_base_trib_w	double precision;

nr_sequencia_trib_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		pr_tributo,
		vl_tributo
	from	dis_pedido_item_trib
	where	nr_sequencia_item = nr_sequencia_item_p;


BEGIN
select	coalesce(qt_material,0),
	coalesce(vl_unitario_material,0),
	coalesce(vl_total_material,0),
	coalesce(vl_desconto,0),
	coalesce(pr_desconto,0)
into STRICT	qt_material_w,
	vl_unitario_material_w,
	vl_total_material_w,
	vl_desconto_w,
	pr_desconto_w
from	dis_pedido_item
where	nr_sequencia = nr_sequencia_item_p;

if (vl_total_material_w > 0) then
	vl_unitario_material_w := dividir(vl_total_material_w,qt_material_w);
elsif (vl_unitario_material_w > 0) then
	begin
	vl_total_material_w := qt_material_w * vl_unitario_material_w;
	if (vl_total_material_w > 0) then
		vl_unitario_material_w := dividir(vl_total_material_w,qt_material_w);
	end if;
	end;
end if;

if (vl_desconto_w > 0) then
	pr_desconto_w := (dividir(vl_desconto_w,vl_total_material_w)*100);
elsif (pr_desconto_w > 0) then
	begin
	vl_desconto_w := dividir((pr_desconto_w * vl_total_material_w),100);
	if (vl_desconto_w > 0) then
		pr_desconto_w := (dividir(vl_desconto_w,vl_total_material_w)*100);
	end if;
	end;
end if;

vl_base_trib_w := (vl_total_material_w - coalesce(vl_desconto_w,0));
open C01;
loop
fetch C01 into
	nr_sequencia_trib_w,
	pr_tributo_w,
	vl_tributo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	vl_tributo_w := dividir((pr_tributo_w * vl_base_trib_w),100);

	update	dis_pedido_item_trib
	set	vl_tributo = vl_tributo_w
	where	nr_sequencia = nr_sequencia_trib_w;
	end;
end loop;
close C01;

select	coalesce(sum(CASE WHEN b.ie_soma_diminui='S' THEN  vl_tributo_w WHEN b.ie_soma_diminui='D' THEN vl_tributo_w * -1  ELSE 0 END ),0)
into STRICT	vl_tributo_w
from	tributo b,
	dis_pedido_item_trib a
where	a.cd_tributo		= b.cd_tributo
and	a.nr_sequencia_item 	= nr_sequencia_item_p;

vl_liquido_material_w := (vl_total_material_w - vl_desconto_w + vl_tributo_w);

update	dis_pedido_item
set	qt_material = qt_material_w,
	vl_unitario_material = vl_unitario_material_w,
	vl_total_material = vl_total_material_w,
	vl_desconto = vl_desconto_w,
	pr_desconto = pr_desconto_w,
	vl_liquido_material = vl_liquido_material_w
where	nr_sequencia = nr_sequencia_item_p;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
