CREATE OR REPLACE PROCEDURE dis_atualizar_valores_pedido (nr_pedido_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
vl_desconto_w	double precision;
pr_desconto_w	double precision;
vl_mercadoria_w	double precision;
vl_total_pedido_w			double precision := 0;
vl_frete_w			double precision := 0;
vl_desconto_ped_w		double precision := 0;
ie_frete_w			varchar(01);
nr_sequencia_item_w		bigint;
qt_prevista_entrega_w		double precision;
vl_liquido_unitario_w		double precision;
vl_cancelado_w			double precision := 0;
vl_cancelado_ww			double precision := 0;
cd_estabelecimento_w		smallint;
ie_desconsidera_cancel_w		varchar(1);
			
C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	dis_pedido_item 
	where	nr_seq_pedido = nr_pedido_p;
	
c02 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	dis_pedido_item a 
	where	a.nr_seq_pedido = nr_pedido_p 
	and	(a.nr_seq_motivo_cancel IS NOT NULL AND a.nr_seq_motivo_cancel::text <> '');


BEGIN 
open C01;
loop 
fetch C01 into	 
	nr_Sequencia_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	CALL dis_atualizar_valores_item(nr_Sequencia_item_w,nm_usuario_p);
end loop;
close C01;
 
select	coalesce(max(pr_desconto),0), 
	coalesce(max(ie_frete),'C'), 
	coalesce(max(vl_frete),0), 
	coalesce(max(vl_desconto),0), 
	max(cd_estabelecimento) 
into STRICT	pr_desconto_w, 
	ie_frete_w, 
	vl_frete_w, 
	vl_desconto_w, 
	cd_estabelecimento_w 
from	dis_pedido 
where	nr_sequencia	= nr_pedido_p;
 
select	coalesce(ie_desconsidera_vl_item_cancel,'N') 
into STRICT	ie_desconsidera_cancel_w 
from	parametro_compras 
where	cd_estabelecimento = cd_estabelecimento_w;
 
select	coalesce(sum(vl_liquido_material),0) 
into STRICT	vl_mercadoria_w 
from	dis_pedido_item 
where	nr_seq_pedido = nr_pedido_p;
 
if (vl_desconto_w > 0) then 
	pr_desconto_w := dividir(vl_desconto_w,vl_mercadoria_w) * 100;
elsif (pr_desconto_w > 0) then 
	vl_desconto_w	:= (vl_mercadoria_w * pr_desconto_w) / 100;
	if (vl_desconto_w > 0) then 
		pr_desconto_w := dividir(vl_desconto_w,vl_mercadoria_w) * 100;
	end if;
end if;
 
vl_total_pedido_w		:= (vl_mercadoria_w - vl_desconto_w);
 
if (ie_frete_w	= 'C') then 
	vl_total_pedido_w	:= (vl_total_pedido_w + vl_frete_w);	
end if;
 
if (ie_desconsidera_cancel_w = 'S') then 
	open C02;
	loop 
	fetch C02 into	 
		nr_sequencia_item_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		select	vl_liquido_material 
		into STRICT	vl_cancelado_w 
		from	dis_pedido_item 
		where	nr_sequencia = nr_sequencia_item_w;
		 
		vl_cancelado_ww	:= vl_cancelado_ww + vl_cancelado_w;
		end;
	end loop;
	close C02;
end if;
 
if (vl_cancelado_ww > 0) then 
	vl_total_pedido_w	:= (vl_total_pedido_w - vl_cancelado_ww);
end if;
 
update dis_pedido 
set	vl_mercadoria = vl_mercadoria_w, 
	vl_total_pedido = vl_total_pedido_w, 
	vl_desconto = vl_desconto_w, 
	pr_desconto = pr_desconto_w 
where	nr_Sequencia = nr_pedido_p;
 
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
