CREATE OR REPLACE PROCEDURE dis_liberar_pedido (nr_pedido_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_gerar_romaneio_liberacao_w varchar(1);

BEGIN
ie_gerar_romaneio_liberacao_w := substr(coalesce(obter_valor_param_usuario(1650, 4, Obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p),'N'),1,1);
 
CALL dis_gerar_pedido_venc(nr_pedido_p,nm_usuario_p);
 
update	dis_pedido a 
set	a.dt_liberacao = clock_timestamp(), 
	a.dt_atualizacao = clock_timestamp(), 
	a.nm_usuario = nm_usuario_p 
where	a.nr_sequencia = nr_pedido_p;
 
if (coalesce(ie_gerar_romaneio_liberacao_w,'N') = 'S') then 
	CALL dis_gerar_romaneio_pedido(nr_pedido_p,nm_usuario_p);
end if;
 
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
