CREATE OR REPLACE PROCEDURE dl_aprovar_distrib_socio ( nr_seq_lote_p bigint, nr_seq_distrib_p bigint, ie_acao_p text, dt_aprovacao_p timestamp, nm_usuario_p text) AS $body$
DECLARE


/*
ie_acao_p
	'A' - aprovar
	'D' - desfazer aprovacao
*/
BEGIN

if (ie_acao_p = 'A') then
	update	dl_distribuicao
	set	dt_aprovacao	= dt_aprovacao_p,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_distrib_p
	and	nr_seq_lote	= nr_seq_lote_p;
elsif (ie_acao_p = 'D') then
	update	dl_distribuicao
	set	dt_aprovacao	 = NULL,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_distrib_p
	and	nr_seq_lote	= nr_seq_lote_p;
end if;

commit;

end;
$body$
