SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE dl_fechar_lote_distrib (nr_seq_lote_p bigint, ie_acao_p text, nm_usuario_p text) AS $body$
DECLARE

/*
ie_acao_p
	'F' - fechado
	'P' - provisorio
*/
BEGIN

if (ie_acao_p = 'F') then
	update	dl_lote_distribuicao
	set	dt_fechamento	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp(),
		ie_status	= 	2
	where	nr_sequencia	= nr_seq_lote_p;
elsif (ie_acao_p = 'P') then
	update	dl_lote_distribuicao
	set	dt_fechamento	 = NULL,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp(),
		ie_status	= 	1
	where	nr_sequencia	= nr_seq_lote_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
