CREATE OR REPLACE PROCEDURE dl_gerar_distribuicao_lote ( nr_seq_lote_p bigint, ie_tipo_socio_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_socio_w	bigint;
ie_tipo_socio_w	varchar(5);

C01 CURSOR FOR
	SELECT	nr_sequencia,
		ie_tipo_socio
	from	dl_socio
	where	ie_situacao	= 'A'
	and (ie_tipo_socio	= ie_tipo_socio_p or coalesce(ie_tipo_socio_p::text, '') = '')
	order by nr_sequencia;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_socio_w,
	ie_tipo_socio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert into dl_distribuicao(
		dt_aprovacao,
		dt_atualizacao,
		dt_atualizacao_nrec,
		ie_tipo_distribuicao,
		nm_usuario,
		nm_usuario_nrec,
		nr_seq_lote,
		nr_seq_socio,
		nr_sequencia,
		vl_calculado,
		vl_distribuicao)
	values (	null,
		clock_timestamp(),
		clock_timestamp(),
		ie_tipo_socio_w,
		nm_usuario_p,
		nm_usuario_p,
		nr_seq_lote_p,
		nr_seq_socio_w,
		nextval('dl_distribuicao_seq'),
		0,
		0);

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
