	values (	nextval('dl_reuniao_socio_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_reuniao_p,
		nr_seq_socio_w,
		'S',
		null,
