CREATE OR REPLACE PROCEDURE do_opsm_integration ( nr_seq_agenda_p bigint, ie_atualiza_opme_int_p text, ie_envia_integracao_p text) AS $body$
DECLARE


nm_usuario_w			varchar(15) 	:= wheb_usuario_pck.get_nm_usuario;
cd_estabelecimento_w	smallint 		:= wheb_usuario_pck.get_cd_estabelecimento;


BEGIN

if (ie_atualiza_opme_int_p = 'S') then
	update	agenda_paciente
	set		ie_opme_integracao = 'S',
			ie_opme_int_status = '0'
	where	nr_sequencia = nr_seq_agenda_p;
end if;

if (ie_envia_integracao_p = 'S') then
	CALL ajusta_status_agenda_int_opme(nr_seq_agenda_p, 9, '', nm_usuario_w);
	CALL enviar_comunic_agend_int_opme(cd_estabelecimento_w, nr_seq_agenda_p, 26, nm_usuario_w, null);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
