SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE dropar_tabela_w () AS $body$
DECLARE


nm_tabela_w			varchar(50);
ds_comando_w			varchar(2000);
C01 CURSOR FOR
	SELECT	table_name
	from	user_tables
	where	substr(table_name,1,12) = 'W_MED_RES_EX'
	and	table_name <> 'W_MED_RES_EXAME_REL'
	
union

	SELECT	table_name
	from	user_tables
	where	substr(table_name,1,10) = 'W_REL_CUBO'
	
union

	Select	table_name
	from	user_tables
	where	substr(table_name,1,10) = 'W_EIS_EVOL'
	and	table_name <> 'W_EIS_EVOL_RELAT';


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	nm_tabela_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ds_comando_w	:= 'Drop table ' || nm_tabela_w;
	CALL exec_sql_dinamico('TASY', ds_comando_w);

END LOOP;
CLOSE C01;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
