CREATE OR REPLACE PROCEDURE drop_view_datamodel (object_name_p user_objects.object_name%type) AS $body$
DECLARE


ds_sql varchar(4000);
object_name_w data_model.object_name%type;


BEGIN

	begin
	select 	max(b.object_name)
	into STRICT 	object_name_w
	from 	user_objects b
	where 	upper(b.object_name) = upper(object_name_p)
	and	b.object_type 	= 'MATERIALIZED VIEW';
	exception
	when 	no_data_found then
		object_name_w := null;
	when 	others then
		object_name_w := null;
	end;	
	
	if (object_name_w IS NOT NULL AND object_name_w::text <> '') then	
		ds_sql := 'drop materialized view ' || object_name_w;
		
		$if dbms_db_version.version >= 11 $then
			EXECUTE ds_sql;
		$end	

	end if;
    	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
