CREATE OR REPLACE PROCEDURE duplicar_atestado_cirurgia ( nr_sequencia_p bigint, cd_pessoa_fisica_p text, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_tipo_atestado_w	bigint;
nr_sequencia_w		bigint;
nr_atendimento_w	bigint;
cd_pessoa_fisica_w	bigint;


BEGIN


select	nextval('atestado_paciente_seq')
into STRICT	nr_sequencia_w
;

select	coalesce(nr_atendimento_p, nr_atendimento),
	cd_pessoa_fisica,
	nr_seq_tipo_atestado
into STRICT	nr_atendimento_w,
	cd_pessoa_fisica_w,
	nr_seq_tipo_atestado_w
from	atestado_paciente
where	nr_sequencia = nr_sequencia_p;

Insert	into atestado_paciente(
	nr_sequencia,
	dt_atestado,
	nr_atendimento,
	dt_atualizacao,
	nm_usuario,
	cd_medico,
	ds_atestado,
	cd_pessoa_fisica,
	nr_seq_tipo_atestado)
values (nr_sequencia_w,
	clock_timestamp(),
	nr_atendimento_w,
	clock_timestamp(),
	nm_usuario_p,
	cd_pessoa_fisica_p,
	' ',
	cd_pessoa_fisica_w,
	nr_seq_tipo_atestado_w);

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
