CREATE OR REPLACE PROCEDURE duplicar_atestado ( nr_sequencia_p bigint, cd_pessoa_fisica_p text, nr_atendimento_p bigint, nm_usuario_p text, nr_seq_novo_atestado_p INOUT bigint) AS $body$
DECLARE


ds_atestado_w		varchar(32000);
nr_seq_tipo_atestado_w	bigint;
nr_sequencia_w		bigint;
nr_atendimento_w	bigint;
nr_seq_atend_cons_pepa_w bigint;
cd_pessoa_fisica_w	bigint;
ie_nivel_atencao_w	varchar(1);
				

BEGIN

ie_nivel_atencao_w := wheb_assist_pck.get_nivel_atencao_perfil;	

IF (wheb_usuario_pck.get_cd_funcao = 381) THEN
	select	nr_seq_atend_cons_pepa
	into STRICT	nr_seq_atend_cons_pepa_w
	from	atestado_paciente
	where	nr_sequencia	=	nr_sequencia_p;	
ELSE
	nr_seq_atend_cons_pepa_w := NULL;
END IF;

select	ds_atestado
into STRICT	ds_atestado_w
from	atestado_paciente
where	nr_sequencia	=	nr_sequencia_p;	

select	nextval('atestado_paciente_seq')
into STRICT	nr_sequencia_w
;

select	coalesce(nr_atendimento_p, nr_atendimento),	
	cd_pessoa_fisica,
	nr_seq_tipo_atestado
into STRICT	nr_atendimento_w,
	cd_pessoa_fisica_w,
	nr_seq_tipo_atestado_w
from	atestado_paciente
where	nr_sequencia = nr_sequencia_p;

Insert	into atestado_paciente(
	nr_sequencia,
	dt_atestado,
	nr_atendimento,
	dt_atualizacao,
	nm_usuario,
	cd_medico,
	ds_atestado,
	cd_pessoa_fisica,
	nr_seq_tipo_atestado,
	ie_situacao,
	ie_nivel_atencao,
	nr_seq_atend_cons_pepa)
values (nr_sequencia_w,
	clock_timestamp(),
	nr_atendimento_w,
	clock_timestamp(),
	nm_usuario_p,
	cd_pessoa_fisica_p,
	ds_atestado_w,
	cd_pessoa_fisica_w,
	nr_seq_tipo_atestado_w,
	'A',
	ie_nivel_atencao_w,
	nr_seq_atend_cons_pepa_w);
	
commit;

nr_seq_novo_atestado_p := nr_sequencia_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
