SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE duplicar_avaliacao ( nr_sequencia_p bigint, nm_usuario_p text, nr_atendimento_p bigint) AS $body$
DECLARE

 
nr_seq_avaliacao_w	bigint;
cd_perfil_ativo_w	bigint := null;


BEGIN 
 
/* Pegar o próximo número de sequencia para a med_avaliacao_paciente */
 
select	nextval('med_avaliacao_paciente_seq') 
into STRICT	nr_seq_avaliacao_w
;
 
if (Obter_perfil_Ativo 	> 0) then 
	cd_perfil_ativo_w	:= Obter_perfil_Ativo;
end if;
 
/* Inservação dos dados na tabela med_avaliacao_paciente */
 
insert	into med_avaliacao_paciente( 
	nr_sequencia, 
	cd_pessoa_fisica, 
	cd_medico, 
	dt_avaliacao, 
	dt_atualizacao, 
	nm_usuario, 
	ds_observacao, 
	nr_seq_tipo_avaliacao, 
	ie_avaliacao_parcial, 
	nr_atendimento, 
	nr_prescricao, 
	nr_seq_aval_compl, 
	nr_seq_ageint, 
	cd_perfil_ativo, 
	nr_seq_consulta, 
	ie_situacao, 
	ie_apresentar_web, 
	nr_seq_checklist_item, 
	nr_seq_atend_checklist, 
	nr_seq_prescr, 
	nr_seq_regulacao, 
	cd_estabelecimento, 
	nr_seq_assinat_inativacao, 
	nr_seq_assinatura, 
	ie_avaliador_aux, 
	nr_seq_pepo, 
	nr_cirurgia, 
	nr_seq_chamado, 
	nr_seq_material, 
	ie_restricao_visualizacao, 
	nr_seq_aval_pre, 
	nr_seq_agenda_cons, 
	nr_seq_contato, 
	nr_seq_agenda_pac, 
	nr_seq_lote, 
	ds_justificativa, 
	nr_seq_com_cliente, 
	nr_seq_checkup) 
SELECT	nr_seq_avaliacao_w, 
	cd_pessoa_fisica, 
	(obter_pessoa_fisica_usuario(nm_usuario_p,'C'))::numeric , 
	clock_timestamp(), 
	clock_timestamp(), 
	nm_usuario_p, 
	ds_observacao, 
	nr_seq_tipo_avaliacao, 
	ie_avaliacao_parcial, 
	nr_atendimento_p, 
	null, 
	nr_seq_aval_compl, 
	nr_seq_ageint, 
	cd_perfil_ativo_w, 
	nr_seq_consulta, 
	'A', 
	ie_apresentar_web, 
	nr_seq_checklist_item, 
	nr_seq_atend_checklist, 
	nr_seq_prescr, 
	nr_seq_regulacao, 
	cd_estabelecimento, 
	nr_seq_assinat_inativacao, 
	nr_seq_assinatura, 
	ie_avaliador_aux, 
	nr_seq_pepo, 
	nr_cirurgia, 
	nr_seq_chamado, 
	nr_seq_material, 
	ie_restricao_visualizacao, 
	nr_seq_aval_pre, 
	nr_seq_agenda_cons, 
	nr_seq_contato, 
	nr_seq_agenda_pac, 
	nr_seq_lote, 
	ds_justificativa, 
	nr_seq_com_cliente, 
	nr_seq_checkup 
from	med_avaliacao_paciente 
where	nr_sequencia = nr_sequencia_p;
 
/* Inserção dos dados na tabela med_avaliacao_result */
 
insert	into med_avaliacao_result( 
	nr_seq_avaliacao, 
	nr_seq_item, 
	dt_atualizacao, 
	nm_usuario, 
	qt_resultado, 
	ds_resultado) 
SELECT	nr_seq_avaliacao_w, 
	a.nr_seq_item, 
	clock_timestamp(), 
	nm_usuario_p, 
	a.qt_resultado, 
	a.ds_resultado 
from	med_avaliacao_result a, 
	med_item_avaliar b 
where	a.nr_seq_avaliacao = nr_sequencia_p 
and	a.nr_seq_item = b.nr_sequencia 
and	coalesce(b.ie_copiar,'S') = 'S';
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
