CREATE OR REPLACE PROCEDURE duplicar_cadastro_cor_estab ( nr_seq_cor_origem_p bigint, cd_estabelecimento_novo_p text, cd_perfil_novo_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_perfil_w		bigint;
cd_estabelecimento_w	bigint;
ie_negacao_w		bigint;
cd_estabelecimento_novo_w	varchar(255);

c01 CURSOR FOR
SELECT	cd_estabelecimento
from	estabelecimento
where	obter_se_contido(cd_estabelecimento,cd_estabelecimento_novo_p) = 'S';

c02 CURSOR FOR
SELECT	a.cd_estabelecimento
from	estabelecimento a
where	not exists (	select	x.cd_estabelecimento
			from	estabelecimento x
			where	obter_se_contido(x.cd_estabelecimento,cd_estabelecimento_novo_w) = 'S'
			and	a.cd_estabelecimento = x.cd_estabelecimento);



BEGIN

select	position('!' in cd_estabelecimento_novo_p)
into STRICT	ie_negacao_w
;



if (ie_negacao_w > 0) then

	cd_estabelecimento_novo_w := substr(cd_estabelecimento_novo_p,2,255);

	open	c02;
	loop
	fetch	c02 into
		cd_estabelecimento_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin

		if (cd_perfil_novo_p = 0) then
			cd_perfil_w	:= null;
		else
			cd_perfil_w	:= cd_perfil_novo_p;
		end if;

		insert into tasy_padrao_cor_perfil(
			nr_sequencia,
			cd_estabelecimento,
			cd_perfil,
			nr_seq_padrao,
			dt_atualizacao,
			nm_usuario,
			ds_cor_fundo,
			ds_cor_fonte,
			ds_cor_selecao,
			dt_atualizacao_nrec,
			nm_usuario_nrec)
		SELECT	nextval('tasy_padrao_cor_perfil_seq'),
			cd_estabelecimento_w,
			cd_perfil_w,
			nr_seq_padrao,
			clock_timestamp(),
			nm_usuario_p,
			ds_cor_fundo,
			ds_cor_fonte,
			ds_cor_selecao,
			clock_timestamp(),
			nm_usuario_p
		from	tasy_padrao_cor_perfil
		where	nr_sequencia	= nr_seq_cor_origem_p;

		commit;

		end;
	end loop;
	close c02;

else

	open	c01;
	loop
	fetch	c01 into
		cd_estabelecimento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		if (cd_perfil_novo_p = 0) then
			cd_perfil_w	:= null;
		else
			cd_perfil_w	:= cd_perfil_novo_p;
		end if;

		insert into tasy_padrao_cor_perfil(
			nr_sequencia,
			cd_estabelecimento,
			cd_perfil,
			nr_seq_padrao,
			dt_atualizacao,
			nm_usuario,
			ds_cor_fundo,
			ds_cor_fonte,
			ds_cor_selecao,
			dt_atualizacao_nrec,
			nm_usuario_nrec)
		SELECT	nextval('tasy_padrao_cor_perfil_seq'),
			cd_estabelecimento_w,
			cd_perfil_w,
			nr_seq_padrao,
			clock_timestamp(),
			nm_usuario_p,
			ds_cor_fundo,
			ds_cor_fonte,
			ds_cor_selecao,
			clock_timestamp(),
			nm_usuario_p
		from	tasy_padrao_cor_perfil
		where	nr_sequencia	= nr_seq_cor_origem_p;

		commit;

		end;
	end loop;
	close c01;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
