CREATE OR REPLACE PROCEDURE duplicar_cad_caixa_opme ( nr_seq_tipo_caixa_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_estabelecimento_w		smallint;
ds_tipo_caixa_w			varchar(80);
nr_seq_fabricante_w		bigint;
ie_situacao_w			varchar(1);
ie_item_estoque_w			varchar(1);
cd_local_estoque_w		smallint;
nr_sequencia_w			bigint;

nr_seq_item_caixa_w		bigint;
cd_material_w			integer;
qt_item_w			bigint;
nr_seq_marca_w			bigint;

c01 CURSOR FOR
SELECT	cd_material,
	qt_item,
	nr_seq_marca
from	opme_tipo_caixa_item
where	nr_seq_tipo = nr_seq_tipo_caixa_p
and	coalesce(dt_exclusao::text, '') = '';


BEGIN

select	cd_estabelecimento,
	ds_tipo_caixa,
	nr_seq_fabricante,
	ie_situacao,
	ie_item_estoque,
	cd_local_estoque
into STRICT	cd_estabelecimento_w,
	ds_tipo_caixa_w,
	nr_seq_fabricante_w,
	ie_situacao_w,
	ie_item_estoque_w,
	cd_local_estoque_w
from	opme_tipo_caixa
where	nr_sequencia = nr_seq_tipo_caixa_p;

select	nextval('opme_tipo_caixa_seq')
into STRICT	nr_sequencia_w
;

insert into opme_tipo_caixa(
	nr_sequencia,
	cd_estabelecimento,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ds_tipo_caixa,
	nr_seq_fabricante,
	ie_situacao,
	ie_item_estoque,
	cd_local_estoque) values (
		nr_sequencia_w,
		cd_estabelecimento_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_tipo_caixa_w,
		nr_seq_fabricante_w,
		ie_situacao_w,
		ie_item_estoque_w,
		cd_local_estoque_w);

open c01;
loop
fetch c01 into
	cd_material_w,
	qt_item_w,
	nr_seq_marca_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	nextval('opme_tipo_caixa_item_seq')
	into STRICT	nr_seq_item_caixa_w
	;

	insert into opme_tipo_caixa_item(
		nr_sequencia,
		nr_seq_tipo,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_material,
		dt_inclusao,
		qt_item,
		nr_seq_marca) values (
			nr_seq_item_caixa_w,
			nr_sequencia_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_material_w,
			clock_timestamp(),
			qt_item_w,
			nr_seq_marca_w);

	end;
end loop;
close c01;

commit;

end;
$body$
