CREATE OR REPLACE PROCEDURE duplicar_cardapio_dia ( nr_seq_cardapio_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_card_w	bigint;


BEGIN

select	nextval('nut_cardapio_dia_seq')
into STRICT	nr_seq_card_w
;

insert into nut_cardapio_dia(
	nr_sequencia,
	cd_estabelecimento,
	dt_atualizacao,
	nm_usuario,
	nr_seq_servico,
	dt_cardapio,
	qt_pessoa_atend,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_nome_cardapio,
	cd_dieta,
	nr_seq_local)
SELECT	nr_seq_card_w,
	cd_estabelecimento_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_servico,
	clock_timestamp(),
	qt_pessoa_atend,
	trunc(clock_timestamp(),'dd'),
	nm_usuario_p,
	nr_seq_nome_cardapio,
	cd_dieta,
	nr_seq_local
from	nut_cardapio_dia
where	nr_sequencia = nr_seq_cardapio_p;

insert into nut_cardapio(
	nr_sequencia,
	nr_seq_card_dia,
	dt_atualizacao,
	nm_usuario,
	nr_seq_comp,
	nr_seq_receita,
	dt_atualizacao_nrec,
	nm_usuario_nrec)
SELECT	nextval('nut_cardapio_seq'),
	nr_seq_card_w,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_comp,
	nr_seq_receita,
	clock_timestamp(),
	nm_usuario_p
from	nut_cardapio
where	nr_seq_card_dia	= nr_seq_cardapio_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
