CREATE OR REPLACE PROCEDURE duplicar_cardapio_periodo (nr_seq_cardapio_p bigint, cd_estabelecimento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_seq_card_w	bigint;
dt_atual_w		timestamp;


BEGIN

dt_atual_w	:=	dt_inicial_p;

while(dt_atual_w <= dt_final_p)loop
	begin

	select	nextval('nut_cardapio_dia_seq')
	into STRICT	nr_seq_card_w
	;

	insert into nut_cardapio_dia(
								 nr_sequencia,
								cd_estabelecimento,
								dt_atualizacao,
								nm_usuario,
								nr_seq_servico,
								dt_cardapio,
								qt_pessoa_atend,
								qt_pessoa_atend_real,
								dt_atualizacao_nrec,
								nm_usuario_nrec)
	SELECT	nr_seq_card_w,
			cd_estabelecimento_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_servico,
			trunc(dt_atual_w,'dd'),
			qt_pessoa_atend,
			qt_pessoa_atend_real,
			trunc(clock_timestamp(),'dd'),
			nm_usuario_p
	from	nut_cardapio_dia
	where	nr_sequencia = nr_seq_cardapio_p;

	insert into nut_cardapio(
							nr_sequencia,
							nr_seq_card_dia,
							dt_atualizacao,
							nm_usuario,
							nr_seq_comp,
							nr_seq_receita,
							dt_atualizacao_nrec,
							nm_usuario_nrec)
	SELECT	nextval('nut_cardapio_seq'),
			nr_seq_card_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_comp,
			nr_seq_receita,
			clock_timestamp(),
			nm_usuario_p
	from	nut_cardapio
	where	nr_seq_card_dia	= nr_seq_cardapio_p;

	dt_atual_w	:= dt_atual_w + 1;
	end;

end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
