CREATE OR REPLACE PROCEDURE duplicar_convenio_pacote_autor (nr_seq_pacote_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_pacote_w	bigint;


BEGIN

select	nextval('convenio_pacote_autor_seq')
into STRICT	nr_seq_pacote_w
;

insert into CONVENIO_PACOTE_AUTOR(nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	cd_estabelecimento,
	cd_convenio,
	ie_prioridade,
	dt_inicio_vigencia,
	dt_fim_vigencia,
	ds_pacote,
	vl_honorario_pacote,
	ie_situacao,
	ie_pacote_unico_pedido)
SELECT	nr_seq_pacote_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	cd_estabelecimento,
	cd_convenio,
	ie_prioridade,
	dt_inicio_vigencia,
	dt_fim_vigencia,
	substr(wheb_mensagem_pck.get_texto(350570)||'-'||ds_pacote,1,255),
	vl_honorario_pacote,
	ie_situacao,
	ie_pacote_unico_pedido
from	convenio_pacote_autor
where	nr_sequencia	= nr_seq_pacote_p;

insert into convenio_pacote_autor_exig(nr_sequencia,
	nr_seq_pacote,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_pacote_exigido,
	dt_inicio_vigencia,
	dt_fim_vigencia)
SELECT	nextval('convenio_pacote_autor_exig_seq'),
	nr_seq_pacote_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_pacote_exigido,
	dt_inicio_vigencia,
	dt_fim_vigencia
from	convenio_pacote_autor_exig
where	nr_seq_pacote	= nr_seq_pacote_p;

insert into convenio_pacote_autor_proc(nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_pacote,
	dt_inicio_vigencia,
	dt_fim_vigencia,
	cd_procedimento,
	ie_origem_proced,
	qt_procedimento,
	tx_pagamento,
	ie_situacao,
	ie_acao_proc_autor,
	ie_acao_duplic_diaria)
SELECT	nextval('convenio_pacote_autor_proc_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_pacote_w,
	dt_inicio_vigencia,
	dt_fim_vigencia,
	cd_procedimento,
	ie_origem_proced,
	qt_procedimento,
	tx_pagamento,
	ie_situacao,
	ie_acao_proc_autor,
	ie_acao_duplic_diaria
from	convenio_pacote_autor_proc
where	nr_seq_pacote	= nr_seq_pacote_p;

insert into convenio_pacote_autor_mat(nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_pacote,
	dt_inicio_vigencia,
	dt_fim_vigencia,
	ie_situacao,
	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material,
	nr_seq_familia,
	cd_material,
	qt_material,
	tx_pagamento)
SELECT	nextval('convenio_pacote_autor_mat_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_pacote_w,
	dt_inicio_vigencia,
	dt_fim_vigencia,
	ie_situacao,
	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material,
	nr_seq_familia,
	cd_material,
	qt_material,
	tx_pagamento
from	convenio_pacote_autor_mat
where	nr_seq_pacote	= nr_seq_pacote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
