CREATE OR REPLACE PROCEDURE duplicar_escala_braden ( nr_sequencia_p bigint, nm_usuario_p text, cd_pessoa_fisica_p text, nr_atendimento_p bigint) AS $body$
BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	insert into atend_escala_braden(
		NR_SEQUENCIA,
		DT_ATUALIZACAO,
		NM_USUARIO     ,
		DT_AVALIACAO    ,
		NR_ATENDIMENTO   ,
		CD_PESSOA_FISICA  ,
		IE_PERCEPCAO_SENSORIAL,
		IE_UMIDADE             ,
		IE_ATIVIDADE_FISICA    ,
		IE_MOBILIDADE          ,
		IE_NUTRICAO            ,
		IE_FRICCAO_CISALHAMENTO,
		QT_PONTO               ,
		DT_ATUALIZACAO_NREC    	,
		NM_USUARIO_NREC        ,
		CD_SETOR_ATENDIMENTO   ,
		DT_LIBERACAO           ,
		IE_SITUACAO            ,
		DT_INATIVACAO          ,
		NM_USUARIO_INATIVACAO  ,
		DS_JUSTIFICATIVA       ,
		CD_PERFIL_ATIVO        ,
		DS_OBSERVACAO          ,
		NR_HORA                )
	SELECT	nextval('atend_escala_braden_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp()    ,
		nr_atendimento_p   ,
		cd_pessoa_fisica_p,
		IE_PERCEPCAO_SENSORIAL,
		IE_UMIDADE             ,
		IE_ATIVIDADE_FISICA    ,
		IE_MOBILIDADE          ,
		IE_NUTRICAO            ,
		IE_FRICCAO_CISALHAMENTO,
		QT_PONTO               ,
		clock_timestamp()    	,
		nm_usuario_p,
		obter_Setor_usuario(nm_usuario_p)   ,
		null          ,
		'A'            ,
		null          ,
		null  ,
		null       ,
		obter_perfil_ativo        ,
		DS_OBSERVACAO          ,
		(to_char(round(clock_timestamp(),'hh24'),'hh24'))::numeric
	from	atend_escala_braden
	where	nr_sequencia = nr_sequencia_p;

	commit;
end if;

end;
$body$
