CREATE OR REPLACE PROCEDURE duplicar_estrut_mat_med (nr_seq_tipo_p bigint, nr_seq_estrutura_p bigint, nm_usuario_p text) AS $body$
DECLARE



cd_material_w			integer;
nr_seq_estrutura_w		bigint;

c01 CURSOR FOR
SELECT	cd_material
from	mat_estrutura_cadastro
where	nr_seq_estrutura	=	nr_seq_estrutura_p;


BEGIN


select  nextval('mat_estrutura_seq')
into STRICT	nr_seq_estrutura_w
;

insert into mat_estrutura(
	nr_sequencia,
	nr_seq_tipo,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ds_estrutura,
	ie_nivel_cadastro,
	nr_seq_estrut_sup,
	ie_situacao)
SELECT	nr_seq_estrutura_w,
	nr_seq_tipo,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	ds_estrutura,
	ie_nivel_cadastro,
	nr_seq_estrut_sup,
	ie_situacao
from	mat_estrutura
where	nr_sequencia	=	nr_seq_estrutura_p
and	nr_seq_tipo	=	nr_seq_tipo_p;

open c01;
loop
fetch c01 into
	cd_material_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	insert into mat_estrutura_cadastro(
		nr_sequencia,
		nr_seq_estrutura,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_material)
	values ( nextval('mat_estrutura_cadastro_seq'),
		nr_seq_estrutura_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_material_w);
end loop;
close c01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
