CREATE OR REPLACE PROCEDURE duplicar_grupo_medicamentos ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_grup_medic_w	bigint;
nr_seq_medic_padrao_w	bigint;
ds_erro_w		varchar(255);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	med_medic_padrao
	where	nr_seq_grupo_medic = nr_sequencia_p;

BEGIN
select	nextval('med_grupo_medic_seq')
into STRICT	nr_seq_grup_medic_w
;

insert into med_grupo_medic(
	nr_sequencia,
	ds_grupo_medic,
	dt_atualizacao,
	nm_usuario,
	ie_situacao,
	cd_estabelecimento,
	cd_medico)
SELECT	nr_seq_grup_medic_w,
	ds_grupo_medic,
	clock_timestamp(),
	nm_usuario_p,
	'A',
	wheb_usuario_pck.get_cd_estabelecimento,
	cd_medico
from	med_grupo_medic
where	nr_sequencia = nr_sequencia_p;

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	nextval('med_medic_padrao_seq')
	into STRICT	nr_seq_medic_padrao_w
	;

	insert into med_medic_padrao(
		nr_sequencia,
		ds_material,
		dt_atualizacao,
		nm_usuario,
		ie_situacao,
		ds_material_selecao,
		ds_generico,
		ds_posologia,
		ie_via_aplicacao,
		ie_tipo_receita,
		ie_exibir_receita,
		ds_forma_apres,
		ds_indicacao,
		ds_contra_indicacao,
		ds_efeito,
		ds_interacao_medicamentosa,
		ds_laboratorio,
		nr_seq_grupo_medic,
		ds_observacao,
		qt_medic_caixa,
		nm_curto,
		cd_estabelecimento,
		ie_uso_medic)
	SELECT	nr_seq_medic_padrao_w,
		ds_material,
		clock_timestamp(),
		nm_usuario_p,
		'A',
		ds_material_selecao,
		ds_generico,
		ds_posologia,
		ie_via_aplicacao,
		ie_tipo_receita,
		ie_exibir_receita,
		ds_forma_apres,
		ds_indicacao,
		ds_contra_indicacao,
		ds_efeito,
		ds_interacao_medicamentosa,
		ds_laboratorio,
		nr_seq_grup_medic_w,
		ds_observacao,
		qt_medic_caixa,
		nm_curto,
		wheb_usuario_pck.get_cd_estabelecimento,
		ie_uso_medic
	from	med_medic_padrao
	where	nr_sequencia = nr_sequencia_w;

	end;
end loop;
close C01;

commit;

end;
$body$
