CREATE OR REPLACE PROCEDURE duplicar_historico_saude ( nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text, nr_atendimento_p bigint default null) AS $body$
DECLARE


/* ie_opcao_p:
MU = Medicamentos em Uso
EF = Exploracao fisica
IR = Interrogatorios
*/
cd_profissional_w	varchar(15);


BEGIN

if (ie_opcao_p = 'MU') then

	Select  max(obter_pf_usuario(nm_usuario_p,'C'))
	into STRICT	cd_profissional_w
	;

	insert	into paciente_medic_uso(
		nr_sequencia,
		cd_pessoa_fisica,
		dt_atualizacao,
		nm_usuario,	
		cd_material,
		cd_unid_med,
		cd_intervalo,
		qt_dose,
		dt_inicio,
		dt_fim,
		ds_reacao,
		ds_observacao,
		ds_medicamento,
		nr_atendimento,
		dt_registro,
		ie_intensidade,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_liberacao,
		nm_usuario_liberacao,
		nr_seq_nivel_seg,
		nr_seq_saep,
		dt_inativacao,
		nm_usuario_inativacao,
		ds_justificativa,
		nr_seq_reg_elemento,
		dt_ultima_dose,
		ie_nao_sabe_inicio,
		ie_nao_sabe_fim,
		ie_nao_sabe_ult_dose,
		ie_nega_medicamentos,
		ie_motivo_termino,
		ie_procedencia,
		ie_auto_medicacao,
		nr_seq_assinatura,
		dt_revisao,
		nm_usuario_revisao,
		cd_profissional)
	SELECT	nextval('paciente_medic_uso_seq'),
		cd_pessoa_fisica,
		clock_timestamp(),
		nm_usuario_p,
		cd_material,
		cd_unid_med,
		cd_intervalo,
		qt_dose,
		dt_inicio,
		dt_fim,
		ds_reacao,
		ds_observacao,
		ds_medicamento,
		CASE WHEN coalesce(nr_atendimento_p,0)=0 THEN nr_atendimento  ELSE nr_atendimento_p END ,
		clock_timestamp(),
		ie_intensidade,
		clock_timestamp(),
		nm_usuario_p,
		null,
		null,
		nr_seq_nivel_seg,
		nr_seq_saep,
		null,
		null,
		null,
		nr_seq_reg_elemento,
		dt_ultima_dose,
		ie_nao_sabe_inicio,
		ie_nao_sabe_fim,
		ie_nao_sabe_ult_dose,
		ie_nega_medicamentos,
		ie_motivo_termino,
		ie_procedencia,
		ie_auto_medicacao,
		null,
		dt_revisao,
		nm_usuario_revisao,
		cd_profissional_w
	from	paciente_medic_uso
	where	nr_sequencia	= nr_sequencia_p;
	
	
elsif (ie_opcao_p = 'EF') then

	Select  max(obter_pf_usuario(nm_usuario_p,'C'))
	into STRICT	cd_profissional_w
	;


	INSERT INTO EXPLORACAO_FISICA( NR_SEQUENCIA,
								DT_REGISTRO,
								DT_ATUALIZACAO, 
								NM_USUARIO, 
								DT_ATUALIZACAO_NREC, 
								NM_USUARIO_NREC, 
								CD_MEDICO, 
								CD_PESSOA_FISICA, 
								NR_ATENDIMENTO, 
								IE_SITUACAO, 								
								DS_HABITO, 
								DS_CABECA, 
								DS_TORAX, 
								DS_ABDOMEN, 
								DS_EXTREMIDADE_SUP, 
								DS_EXTREMIDADE_INF, 
								DS_GENITAIS, 
								DS_ODONTOLOGICO, 
								DS_NUTRICAO, 
								DS_PSICOLOGICO, 
								DS_OUTROS_PROFISSIONAIS, 								 
								DS_PESCOCO)
						SELECT	nextval('exploracao_fisica_seq'),
								clock_timestamp(),
								clock_timestamp(),
								nm_usuario_p,
								clock_timestamp(),
								nm_usuario_p,
								cd_profissional_w, 
								CD_PESSOA_FISICA, 
								nr_atendimento_p, 
								'A', 								
								DS_HABITO, 
								DS_CABECA, 
								DS_TORAX, 
								DS_ABDOMEN, 
								DS_EXTREMIDADE_SUP, 
								DS_EXTREMIDADE_INF, 
								DS_GENITAIS, 
								DS_ODONTOLOGICO, 
								DS_NUTRICAO, 
								DS_PSICOLOGICO, 
								DS_OUTROS_PROFISSIONAIS, 								 
								DS_PESCOCO
						from	exploracao_fisica
						where	nr_sequencia	= nr_sequencia_p;


elsif (ie_opcao_p = 'IR') then	

		Select  max(obter_pf_usuario(nm_usuario_p,'C'))
		into STRICT	cd_profissional_w
		;

		INSERT INTO INTERROGATORIO_APARELHO( NR_SEQUENCIA,
									  DT_REGISTRO,
									  DT_ATUALIZACAO,
									  NM_USUARIO, 
									  DT_ATUALIZACAO_NREC, 
									  NM_USUARIO_NREC, 
									  CD_MEDICO, 
									  CD_PESSOA_FISICA, 									  
									  NR_ATENDIMENTO, 
									  IE_SITUACAO, 									  
									  DS_SINAIS_SINTOMAS, 
									  DS_APARELHO_CARDIO, 
									  DS_APARELHO_RESP, 
									  DS_APARELHO_DIGES, 
									  DS_SISTEMA_NEUFRO, 
									  DS_SISTEMA_ENDOCRINO, 
									  DS_SISTEMA_HEMATO, 
									  DS_SISTEMA_NERVOSO, 
									  DS_SISTEMA_ESQUELETICO, 
									  DS_PELE, 
									  DS_SENTIDO_ORGAO, 
									  DS_ESFERA,
									  DS_CONDICAO_ATUAL) 
							SELECT	  nextval('interrogatorio_aparelho_seq'),
									  clock_timestamp(),
									  clock_timestamp(),
									  nm_usuario_p,
									  clock_timestamp(),
									  nm_usuario_p,
									  cd_profissional_w, 
									  CD_PESSOA_FISICA, 
									  nr_atendimento_p, 
									  'A', 								
									  DS_SINAIS_SINTOMAS, 
									  DS_APARELHO_CARDIO, 
									  DS_APARELHO_RESP, 
									  DS_APARELHO_DIGES, 
									  DS_SISTEMA_NEUFRO, 
									  DS_SISTEMA_ENDOCRINO, 
									  DS_SISTEMA_HEMATO, 
									  DS_SISTEMA_NERVOSO, 
									  DS_SISTEMA_ESQUELETICO, 
									  DS_PELE, 
									  DS_SENTIDO_ORGAO, 
									  DS_ESFERA,
									  DS_CONDICAO_ATUAL
							from	INTERROGATORIO_APARELHO
							where	nr_sequencia	= nr_sequencia_p;
						
	
end if;

commit;

end;
$body$
