CREATE OR REPLACE PROCEDURE duplicar_imagem_ocup_estab ( nr_seq_imagem_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_imagem_w	varchar(10000);
qt_imagem_w	bigint;
ie_status_w	varchar(15);


BEGIN

select	ds_imagem,
	ie_status
into STRICT	ds_imagem_w,
	ie_status_w
from	ocup_hosp_status
where	nr_sequencia	= nr_seq_imagem_p;

select	count(*)
into STRICT	qt_imagem_w
from	ocup_hosp_status
where	ie_status		= ie_status_w
and	cd_estabelecimento	= cd_estabelecimento_p;

if (qt_imagem_w	= 0) then
	insert into ocup_hosp_status(nr_sequencia,
		cd_estabelecimento,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_status,
		ds_imagem,
		nr_seq_apresent,
		ds_status_mapa,
		nr_status_ocup)
	SELECT	nextval('ocup_hosp_status_seq'),
		cd_estabelecimento_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ie_status,
		ds_imagem_w,
		nr_seq_apresent,
		ds_status_mapa,
		nr_status_ocup
	from	ocup_hosp_status
	where	nr_sequencia	= nr_seq_imagem_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
