CREATE OR REPLACE PROCEDURE duplicar_linha_capa ( nr_seq_capa_p bigint, nr_sequencia_p bigint, nr_seq_superior_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;
nr_sequencia_ww	bigint;

C01 CURSOR FOR
SELECT	nr_sequencia
from	capa_ficha_financ_lin
where	nr_seq_superior = nr_sequencia_p;


BEGIN
select	nextval('capa_ficha_financ_lin_seq')
into STRICT	nr_sequencia_w
;

insert into capa_ficha_financ_lin(
	nr_sequencia,
	nr_seq_superior,
	nr_seq_capa,
	qt_decimais,
	ds_linha,
	nm_usuario,
	nm_usuario_nrec,
	dt_atualizacao,
	dt_atualizacao_nrec)
SELECT	nr_sequencia_w,
	nr_seq_superior_p,
	nr_seq_capa_p,
	qt_decimais,
	ds_linha,
	nm_usuario_p,
	nm_usuario_p,
	clock_timestamp(),
	clock_timestamp()
from	capa_ficha_financ_lin
where	nr_sequencia = nr_sequencia_p;

open C01;
loop
fetch C01 into
	nr_sequencia_ww;
EXIT WHEN NOT FOUND; /* apply on C01 */
	CALL duplicar_linha_capa( nr_seq_capa_p, nr_sequencia_ww, nr_sequencia_w, nm_usuario_p);
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
