CREATE OR REPLACE PROCEDURE duplicar_log_fase1_atualizacao (nr_seq_atualizacao_atual_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_atualizacao_ant_w	bigint;
nr_sequencia_w			bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	Log_Atualizacao a
	where	a.nr_seq_atualizacao = nr_seq_atualizacao_ant_w
	and 	ie_evento in ('010','020','030','040','050','060');




BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_atualizacao_ant_w
from	atualizacao_versao
where	nr_sequencia <> nr_seq_atualizacao_atual_p;

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		insert into Log_Atualizacao(
		nr_sequencia,
		nr_seq_atualizacao,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_evento,
		dt_evento,
		dt_fim_evento)
	SELECT	nextval('log_atualizacao_seq'),
		nr_seq_atualizacao_atual_p,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario,
		ie_evento,
		dt_evento,
		dt_fim_evento
	from	Log_Atualizacao
	where	nr_sequencia = nr_sequencia_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
