CREATE OR REPLACE PROCEDURE duplicar_material_restricao ( nr_sequencia_p bigint, cd_material_p bigint, nm_usuario_p text, nr_sequencia_out_p INOUT bigint ) AS $body$
DECLARE


nr_sequencia_out_w			bigint;


BEGIN

select	coalesce(max(nr_sequencia),0) + 1
into STRICT	nr_sequencia_out_w
from 	material_restricao
where	cd_material = cd_material_p;	

insert into material_restricao(
		cd_material,
		nr_sequencia,
		ds_restricao,
		qt_minimo,
		qt_maximo,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec)
SELECT	cd_material,
		nr_sequencia_out_w,
		ds_restricao,
		qt_minimo,
		qt_maximo,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p
from	material_restricao
where	nr_sequencia = nr_sequencia_p
and		cd_material = cd_material_p;

commit;

nr_sequencia_out_p := nr_sequencia_out_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
